import React from "react";
import PropTypes from "prop-types";
//Styles
import styled from "styled-components";

function variantModifier(theme) {
  return {
    primary: `
    color: ${theme.components.button.primaryColor};
    background-color: ${theme.components.button.primaryBackgroundColor};
    border: 1px solid ${theme.components.button.primaryBorderColor};
    &:hover {
      color:${theme.components.button.primaryHoverColor};
      background-color: ${theme.components.button.primaryHoverBackgroundColor};
    }
    &:focus {
      outline: 2px solid ${theme.components.button.primaryFocus};
    }
    &:before {
      background-color: ${theme.components.button.primaryIconBackgroundColor};
    }
    &:after {
      color: ${theme.components.button.primaryIconColor};
    }
  `,
    dark: `
    color: ${theme.components.button.darkColor};
    background-color: ${theme.components.button.darkBackgroundColor};
    border: 1px solid ${theme.components.button.darkBorderColor};
    &:hover {
      color: ${theme.components.button.darkHoverColor};
      background-color:${theme.components.button.darkHoverBackgroundColor};
    }
    &:focus {
      outline: 2px solid ${theme.components.button.darkFocus};
    }
    &:before {
      background-color: ${theme.components.button.darkIconBackgroundColor};
    }
    &:after {
      color: ${theme.components.button.darkIconColor};
    }
  `,
    light: `
    color: ${theme.components.button.lightColor};
    background-color: ${theme.components.button.lightBackgroundColor};
    border: 1px solid ${theme.components.button.lightBorderColor};
    &:hover {
      color: ${theme.components.button.lightHoverColor};
      background-color: ${theme.components.button.lightHoverBackgroundColor};
    }
    &:focus {
      outline: 2px solid ${theme.components.button.lightFocus};
    }
    &:before {
      background-color: ${theme.components.button.lightIconBackgroundColor};
    }
    &:after {
      color: ${theme.components.button.lightIconColor};
    }
  `,
    default: `
    color: ${theme.components.button.color};
    background-color: ${theme.components.button.backgroundColor};
    border: 1px solid ${theme.components.button.borderColor};
    &:hover {
      color: ${theme.components.button.hoverColor};
      background-color: ${theme.components.button.hoverBackgroundColor};
    }
    &:focus {
      background-color:  ${theme.components.button.focus};
    }
    &:before {
      background-color: ${theme.components.button.iconBackgroundColor};
    }
    &:after {
      color: ${theme.components.button.iconColor};
    }
  `
  };
}

const Button = styled.button`
  /*
  -- Variant
  */
  ${props => variantModifier(props.theme)[props.variant]}
  /*
  -- Shared default Styles
  */
  font-family: ${props => props.theme.type.base};
  font-size: ${props =>
    props.fontSize || props.theme.components.button.fontSize};
  font-weight: ${props =>
    props.fontWeight || props.theme.components.button.fontWeight};
  padding: ${props => props.padding || props.theme.components.button.padding};
  line-height: ${props =>
    props.lineHeight || props.theme.components.button.lineHeight};
  /*
  -- General props
  */
  &:hover {
    color: ${props => props.hoverColor};
    background-color: ${props => props.hoverBackgroundColor};
  }
  &:focus {
    color: ${props => props.focusColor};
    background-color:  ${props => props.focusBackgroundColor};
  }
  border: ${props => props.border};
  color: ${props => props.color};
  background-color: ${props => props.backgroundColor};
  position: relative;
  border-radius: 0;
  cursor: pointer;
  /*
  -- Before & After for cutting effect
  */
  &:before,
  &:after {
    height: 36px;
    width: 36px;
    position: absolute;
    top: -20px;
    right: -19px;
  }

  &:before {
    content: "";
    transform: rotate(45deg);
    background-color: ${props => props.backgroundColor};
  }

  &:after {
    content: '${props => props.theme.icons.plus}';
    color: ${props => props.contentColor || props.color};
    font-size: 22px;
    font-weight: 100;
    display: flex;
    align-items: center;
    justify-content: center;
    background-color: transparent;
  }
  /*
  -- If needed for tag a
  */
  display: ${props => props.display}; //inline-flex
  width: ${props => props.width};
  text-decoration: ${props => props.textDecoration}; //none
`;

const types = {
  children: PropTypes.node,
  variant: PropTypes.string,
  fontSize: PropTypes.string,
  fontWeight: PropTypes.string,
  padding: PropTypes.string,
  lineHeight: PropTypes.string,
  hoverColor: PropTypes.string,
  hoverBackgroundColor: PropTypes.string,
  focusColor: PropTypes.string,
  focusBackgroundColor: PropTypes.string,
  border: PropTypes.string,
  color: PropTypes.string,
  backgroundColor: PropTypes.string,
  contentColor: PropTypes.string,
  display: PropTypes.string,
  width: PropTypes.string,
  textDecoration: PropTypes.string
};

const defaults = {
  variant: "default"
};

Button.propTypes = types;
Button.defaultProps = defaults;

export default Button;

//Only used for the Storybook Props Table
export const WithProps = ({ children, ...props }) => (
  <Button {...props}>{children}</Button>
);

WithProps.propTypes = types;
WithProps.defaultProps = defaults;
import React from "react";
import PropTypes from "prop-types";
//Styles
import styled from "styled-components";

const AnimatedBackground = styled.div`
  @keyframes placeHolderShimmer {
    0% {
      background-position: -468px 0;
    }
    100% {
      background-position: 468px 0;
    }
  }
  animation-duration: 1s;
  animation-fill-mode: forwards;
  animation-iteration-count: infinite;
  animation-name: placeHolderShimmer;
  animation-timing-function: linear;
  background: ${props =>
    props.theme.components.animatedBackground.backgroundColor};
  background: linear-gradient(
    to right,
    ${props => props.theme.components.animatedBackground.gradientColor[0]} 8%,
    ${props => props.theme.components.animatedBackground.gradientColor[1]} 18%,
    ${props => props.theme.components.animatedBackground.gradientColor[2]} 33%
  );
  background-size: 936px 104px;
  height: ${props => props.height};
  width: ${props => props.width};
  position: relative;
`;

const types = {
  height: PropTypes.string,
  width: PropTypes.string,
  children: PropTypes.node
};

AnimatedBackground.propTypes = types;

export default AnimatedBackground;

//Only used for the Storybook Props Table
export const WithProps = ({ children, ...props }) => (
  <AnimatedBackground {...props}>{children}</AnimatedBackground>
);

WithProps.propTypes = types;
import React from "react";
import PropTypes from "prop-types";
import styled from "styled-components";

const variantModifier = {
  /* 16:9 Aspect Ratio */
  sixteenByNine: `
    padding-top: 56.25%;
  `,
  /* 4:3 Aspect Ratio */
  fourByTree: `
    padding-top: 75%;
  `,
  /* 3:2 Aspect Ratio */
  treeByTwo: `
    padding-top: 66.66%;
  `,
  /* 8:5 Aspect Ratio */
  eightByFive: `
    padding-top: 62.5%;
  `,
  /* 1:1 Aspect Ratio  */
  oneByOne: `
    padding-top: 100%;
  `
};

const AspectRatio = ({ children, className }) => {
  return (
    <div className={className}>
      <div className="aspRTO">{children}</div>
    </div>
  );
