      lg: 768
    }
  }
};

function isEmpty(str) {
  return !str || 0 === str.length;
}

function Picture({
  src,
  className,
  alt,
  size,
  useHeight,
  absoluteSize,
  onCatchError,
  hasResize,
  ...props
}) {
  const { imageAPI, portalWWW } = useConfig();

  const { sizes } = breakpoints;

  const onHandleSrc = size => {
    const param = useHeight ? "height" : "width";

    if (!isEmpty(src)) {
      if (!hasResize) return src;

      if (src.includes("http")) {
        return `${imageAPI}/image/resizer?imageUrl=${encodeURIComponent(
          src
        )}&${param}=${size}`;
      } else {
        return `${imageAPI}/image/resizer?imageUrl=${portalWWW}/${encodeURIComponent(
          src
        )}&${param}=${size}`;
      }
    }
  };

  // When the absoluteSize prop is defined it uses that size for the image query
  const isAbsolute = useMemo(() => !!absoluteSize, [absoluteSize]);
  const defaultSrc = useMemo(() => {
    if (!isAbsolute) return onHandleSrc(sizes[size].lg);
    return onHandleSrc(absoluteSize);
  }, [isAbsolute, src]);

  const pictureProps = {
    height: props.height,
    onClick: props.onClick
  };

  const onError = e => {
    e.target.style.display = "none";
    onCatchError(e);
  };

  return (
    <div className={className}>
      <picture>
        {!isAbsolute &&
          breakpoints.names.map(breakpoint => (
            <source
              key={breakpoint}
              media={`(max-width: ${breakpoints.mediaQueries[breakpoint]}px)`}
              srcSet={onHandleSrc(sizes[size][breakpoint])}
              {...pictureProps}
            />
          ))}
        <img src={defaultSrc} alt={alt} onError={onError} {...pictureProps} />
      </picture>
    </div>
  );
}

