const LinkExternal = ({ href, children, activeColor, hasPop }) => {
  const [isHover, setHover] = useState(false);
  const cleanUrl = useMemo(
    () => href.replace(/^(?:https?:\/\/)?(?:www\.)?/i, ""),
    [href]
  );

  return (
    <Link
      href={href}
      target="_blank"
      onMouseEnter={() => setHover(true)}
      onMouseLeave={() => setHover(false)}
      className="link-external"
      rel="noopener"
      native
    >
      {children}
      {hasPop && isHover && (
        <Portal>
          <Helper activeColor={activeColor}>{cleanUrl}</Helper>
        </Portal>
      )}
    </Link>
  );
};

