//Components
import { Text, Icon } from "../../../atoms/index";

const AgendaCard = ({
  children,
  className,
  category,
  title,
  text,
  date,
  href,
  target,
  categoryColor,
  titleColor,
  textColor,
  dateColor,
  iconColor,
  hasDateSlider,
  hasText,
  theme,
  agendaCardRef
}) => {
  const [isSlideRight, setSlideRight] = useState(false);
  const [isSlideLeft, setSlideLeft] = useState(false);
  const agendaCardLeft = useRef(null);
  const sliderContainer = useRef(null);

  const slideRight = e => {
    e.preventDefault();
    if (isSlideLeft) {
      sliderContainer.current.style.transform = "translateX(-45px)";
      setSlideRight(true);
      setSlideLeft(false);
      agendaCardLeft.current.classList.remove("slide-is-right");
      agendaCardLeft.current.classList.remove("slide-is-left");
    } else {
      sliderContainer.current.style.transform = "translateX(-90px)";
      setSlideRight(true);
      setSlideLeft(false);
      agendaCardLeft.current.classList.add("slide-is-right");
      agendaCardLeft.current.classList.remove("slide-is-left");
    }
  };
  const slideLeft = e => {
    e.preventDefault();
    if (isSlideRight) {
      sliderContainer.current.style.transform = "translateX(-45px)";
      setSlideLeft(true);
      setSlideRight(false);
      agendaCardLeft.current.classList.remove("slide-is-right");
      agendaCardLeft.current.classList.remove("slide-is-left");
    } else {
      sliderContainer.current.style.transform = "translateX(0)";
      setSlideLeft(true);
      setSlideRight(false);
      agendaCardLeft.current.classList.add("slide-is-left");
      agendaCardLeft.current.classList.remove("slide-is-right");
    }
  };

  return (
    <a
      className={className}
      href={href}
      target={target}
      ref={agendaCardRef}
      tabIndex="0"
    >
      <div className="row no-gutters">
        <div className="col-auto">
          <div ref={agendaCardLeft} className="agenda-card-left">
            {hasDateSlider ? (
              <div className="row no-gutters">
                <div className="col-auto">
                  <div className="arrow-container">
                    <button
                      onClick={slideLeft}
                      aria-label="Ir para a data inicial"
                    >
                      <Icon
                        className="fa-chevron-left"
                        name={theme.icons.chevron_left}
                        color={
                          iconColor || theme.components.agendaCard.iconColor
                        }
                      />
                    </button>
                  </div>
                </div>
                <div className="col">
                  <div className="text">
                    <div ref={sliderContainer} className="text-slider">
                      {children}
                    </div>
                  </div>
                </div>
                <div className="col-auto">
                  <div className="arrow-container">
                    <button
                      onClick={slideRight}
                      aria-label="Ir para a data final"
                    >
                      <Icon
                        className="fa-chevron-right"
                        name={theme.icons.chevron_right}
                        color={
                          iconColor || theme.components.agendaCard.iconColor
                        }
                      />
                    </button>
                  </div>
                </div>
              </div>
            ) : (
              <Fragment>{children}</Fragment>
            )}
          </div>
        </div>
        <div className="col">
          <div className="agenda-card-right">
            <Text
              size="article"
              text={category}
              color={categoryColor || theme.components.agendaCard.categoryColor}
              fontWeight="300"
            />
            <Text
              as="h3"
              text={title || theme.components.agendaCard.titleColor}
              size="article"
              fontWeight="400"
              color={titleColor}
              marginBottom="0"
            />
            <div className="bottom-content">
              {hasText && (
                <Text
                  text={text}
                  color={textColor || theme.components.agendaCard.textColor}
                  size="article"
                  fontWeight="300"
                />
              )}
              <Text
                textTransform="lowercase"
                text={date}
                color={dateColor || theme.components.agendaCard.dateColor}
                size="xSmall"
                fontWeight="300"
              />
            </div>
          </div>
        </div>
      </div>
    </a>
  );
};

