import React, { useState, Fragment } from "react";
import PropTypes from "prop-types";
import styled, { withTheme } from "styled-components";

import ShareSocialMedia from "../Cards/ShareSocialMedia";

import { Icon, ClickAwayListener } from "../../atoms";
import Portal from "../../../containers/Portal";
import { lighten } from "polished";
import { useConfig } from "../../../context/ConfigContext";

const SocialMediaActions = ({
  className,
  children,
  size,
  iconColor,
  share,
  theme
}) => {
  const [isOpen, setOpen] = useState(false);

  const { useIntl } = useConfig();
  const intl = useIntl();

  const showShareIcons = e => {
    e.preventDefault();
    e.stopPropagation();
    setOpen(true);
  };

  const hideShareIcons = e => {
    e.preventDefault();
    e.stopPropagation();
    setOpen(false);
  };

  const socialProps =
    size.width > 575
      ? {
          onMouseEnter: showShareIcons,
          onMouseLeave: hideShareIcons
        }
      : {};
  return (
    <div className={className}>
      <div className="social-media-container" {...socialProps}>
        <button
          onClick={showShareIcons}
          tabIndex="0"
          aria-label={intl({
            id: "app.socialMediaActions.button.description",
            defaultMessage: "Share Button"
          })}
        >
          {children}
          <Icon name={theme.icons.share_alt} color={iconColor} size="xSmall" />
        </button>
        {/*The order is important for tab order and accessibility*/}
        {isOpen && (
          <Fragment>
            {size.width <= 575 ? (
              <Portal>
                <ClickAwayListener onChange={() => setOpen(false)}>
                  <ShareSocialMedia share={share} />
                </ClickAwayListener>
              </Portal>
            ) : (
              <ShareSocialMedia share={share} />
            )}
          </Fragment>
        )}
      </div>
    </div>
  );
};

const defaults = {
  share: {
    facebook: `https://www.facebook.com/sharer/sharer.php?u=url`,
    twitter: `http://twitter.com/share?url=url&text=title`,
    linkedin: `https://www.linkedin.com/shareArticle?mini=true&url=url`,
    url: "/"
  }
};

const types = {
  className: PropTypes.string,
  children: PropTypes.node,
  iconColor: PropTypes.string,
  size: PropTypes.object,
  share: PropTypes.object,
  intl: PropTypes.string,
  theme: PropTypes.object,
  ariaLabel: PropTypes.string
};

SocialMediaActions.propTypes = types;
SocialMediaActions.defaultProps = defaults;

const SocialMediaActionsWithStyles = styled(SocialMediaActions)`
  button {
    background-color: transparent;
    border: none;
    cursor: pointer;
    height: 24px;
    width: 24px;
    &:hover {
      i {
        color: ${props => lighten(0.5, props.iconColor || "#000")};
      }
    }
    &:focus {
      ${props => props.theme.mixins.focus};
    }
  }
  .social-media-container {
    position: relative;
  }
`;

