import React from "react";
import PropTypes from "prop-types";
import styled, { withTheme } from "styled-components";
import {
  AspectRatio,
  LineTruncate,
  Link,
  Picture,
  Text,
  Triangle,
  ContentContainer
} from "../../../atoms";
import SocialMediaActions from "../../SocialMediaActions";
import { usePalette } from "react-palette";

const ColorExtractCard = ({
  className,
  alt,
  src,
  share,
  size,
  category,
  title,
  date,
  isOpen,
  theme
}) => {
  //data: {darkMuted, darkVibrant, lightMuted, lightVibrant, muted, vibrant}
  const { data, loading, error } = usePalette(src);

  //Convert hex Color to rgba
  const hex2rgba = (hex, alpha = 1) => {
    const [r, g, b] = hex.match(/\w\w/g).map(x => parseInt(x, 16));
    return `rgba(${r},${g},${b},${alpha})`;
  };

  const dominantColor = hex2rgba(`${data.lightMuted}`, 0.6);
  const defaultColor = hex2rgba(
    `${theme.components.colorExtractCard.backgroundColor}`,
    0.5
  );

  return (
    <div className={className}>
      <ContentContainer
        className="content-container"
        backgroundColor={
          !loading && !error
            ? theme.highContrast.lightOpacityBgColor || dominantColor
            : defaultColor
        }
      >
        <Triangle
          className="triangle"
          transform="rotate(90deg)"
          withContent={true}
          color={
            !loading && !error
              ? theme.highContrast.lightOpacityBgColor || dominantColor
              : defaultColor
          }
          size="22px"
          top="calc(50% - 11px)"
          right="-33px"
          contentSize="24px"
          zIndex="1"
        />
        <div className="d-flex flex-column justify-content-between h-100">
          <div>
            <Link
              text={category}
              fontWeight="300"
              lineHeight="1.4"
              mobileXsFontSize="12px"
              color={theme.components.colorExtractCard.linkColor}
            />
            <Text
              isSerif
              as="h3"
              size="large"
              sizeXsSize="small"
              fontWeight="400"
              lineHeight="1.5"
              text={title}
              marginTop="10px"
            />
          </div>
          <div className="row">
            <div className="col">
              <LineTruncate>
                <Text text={date} size="xSmall" textTransform="lowercase" />
              </LineTruncate>
            </div>
            <div className="col-auto">
              <SocialMediaActions
                size={size}
                iconColor={theme.components.colorExtractCard.iconColor}
                share={share}
                isOpen={isOpen}
              />
            </div>
          </div>
        </div>
      </ContentContainer>
      <AspectRatio
        ratio="sixteenByNine"
        backgroundColor={theme.components.colorExtractCard.backgroundColor}
      >
        <Picture src={src} alt={alt} height="100%" />
      </AspectRatio>
    </div>
  );
};

ColorExtractCard.propTypes = {
  size: PropTypes.object,
  share: PropTypes.object,
