  const { link: Link } = useConfig();

  const isExternalLink =
    href !== null && href.includes("http") && !href.includes("www.ua");

  return (
    <Link href={href} target={isExternalLink ? "_blank" : "_self"} {...props} />
  );
};

LinkWrapper.propTypes = {
  href: PropTypes.string.isRequired
};

