      },
      h5: {
        component: Header,
        props: {
          as: "h5"
        }
      },
      p: {
        component: Text,
        props: {
          color: "#000",
          fontWeight: "300"
        }
      },
      a: {
        component: LinkWrapper,
        props: {
          fontWeight: "400",
          fontSize: "15px",
          paddingBottom: "5px",
          native: true
        }
      },
      ...imageOption
    }
  };

  return (
    <MarkdownStyles
      activeColor={activeColor}
      isInline={isInline}
      isThemeReverse={isThemeReverse}
      className={className}
      {...props}
    >
      <Markdown className="markdown" options={markdownOptions}>
        {content}
      </Markdown>
    </MarkdownStyles>
  );
};

MarkdownRenderer.propTypes = {
  content: PropTypes.string.isRequired,
  activeColor: PropTypes.string,
  isInline: PropTypes.bool,
  isThemeReverse: PropTypes.bool,
  className: PropTypes.string,
  default: PropTypes.bool,
  isMobile: PropTypes.bool
};

MarkdownRenderer.defaultProps = {
