const SectionHeader = ({
  textColor,
  title,
  children,
  marginBottom,
  hasLine,
  hasLink,
  href,
  hasContainer,
  tag,
  mobileSmFontSize,
  mobileXsFontSize,
  fontSize,
  outlineOffset,
  lineMarginTop,
  lineHeight,
  mobileMdColor,
  externalLink,
  fontWeight
}) => {
  const [isHover, setHover] = useState(false);

  return (
    <div className={`Section-Header ${hasContainer ? "container" : ""}`}>
      <ContentContainer
        marginBottom={marginBottom || "50px"}
        onMouseEnter={() => setHover(true)}
        onMouseLeave={() => setHover(false)}
        width="fit-content"
        pointer
      >
        {hasLink ? (
          <Link
            fontDecoration="none"
            href={href}
            arial-label={title || children}
            display="inline-block"
            outlineOffset={outlineOffset}
            native={externalLink}
          >
            <Title
              fontSize={fontSize}
              color={textColor}
              fontWeight={fontWeight || "500"}
              tag={tag}
              mobileSmFontSize={mobileSmFontSize}
              mobileXsFontSize={mobileXsFontSize}
              mobileMdColor={mobileMdColor}
            >
              {title || children}
            </Title>
          </Link>
        ) : (
          <Title
            tabIndex={0}
            fontSize={fontSize}
            color={textColor}
            fontWeight={fontWeight || "500"}
            arial-label={title || children}
            tag={tag}
            mobileSmFontSize={mobileSmFontSize}
            mobileXsFontSize={mobileXsFontSize}
            mobileMdColor={mobileMdColor}
            cursor="default"
            display="inline-block"
            lineHeight={lineHeight}
          >
            {title || children}
          </Title>
        )}
        {(hasLink || hasLine) && (
          <Line
            className="Section-Header-Line"
            color={textColor}
            isHover={isHover}
            marginTop={lineMarginTop}
          />
        )}
      </ContentContainer>
    </div>
  );
};

SectionHeader.propTypes = {
  textColor: PropTypes.string,
  fontSize: PropTypes.string,
  title: PropTypes.oneOfType([PropTypes.string, PropTypes.node]),
  children: PropTypes.node,
  marginBottom: PropTypes.string,
  hasLine: PropTypes.bool,
  hasLink: PropTypes.bool,
  href: PropTypes.string,
  hasContainer: PropTypes.bool,
  tag: PropTypes.string,
  mobileSmFontSize: PropTypes.string,
  mobileXsFontSize: PropTypes.string,
  outlineOffset: PropTypes.string,
  lineMarginTop: PropTypes.string,
  mobileMdColor: PropTypes.string,
  lineHeight: PropTypes.string,
  externalLink: PropTypes.bool,
  fontWeight: PropTypes.string
};

