const LayeredCard = ({
  theme,
  textColor,
  backgroundColor,
  alt,
  src,
  showPlusButton,
  inverted,
  title,
  href,
  links,
  externalLink,
  description,
  hasText,
  isMarkdown,
  activeColor
}) => {
  const { formattedMessage: FormattedMessage } = useConfig();
  const {
    textColor: themeTextColor,
    linkColor: themeLinkColor,
    backgroundColor: themeBgColor
  } = theme.components.layeredCard;
  return (
    <Fragment>
      <CardContainer
        backgroundColor={
          theme.highContrast.backgroundColor || backgroundColor || themeBgColor
        }
      >
        <PositionContainer>
          <div className="container">
            <TextContainer
              paddingLeft={inverted ? "70px" : "0"}
              paddingRight={inverted ? "0" : "70px"}
              xsPaddingLeft={inverted ? "30px" : "0"}
              xsPaddingRight={inverted ? "0" : "30px"}
              float={inverted ? "right" : "left"}
            >
              <ContentContainer position="relative" zIndex="2">
                <SectionHeader
                  href={href}
                  tag="h3"
                  fontSize="28px"
                  mobileSmFontSize="26px"
                  mobileXsFontSize="20px"
                  hasContainer={false}
                  hasLine={true}
                  hasLink={true}
                  textColor={
                    theme.highContrast.link || textColor || themeTextColor
                  }
                  marginBottom="15px"
                  externalLink={externalLink}
                >
                  {title}
                </SectionHeader>
                <OverflowContainer>
                  {hasText ? (
                    isMarkdown ? (
                      <MarkdownRenderer
                        content={description}
                        activeColor={activeColor}
                      />
                    ) : (
                      <Text
                        lineHeight="1.6"
                        size="article"
                        fontWeight="300"
                        text={description}
                      />
                    )
                  ) : (
                    <VerticalList className="campus-items">
                      {links.map((item, index) => (
                        <ListItem key={index} marginBottom="5px">
                          <Link
                            size="medium"
                            fontWeight="400"
                            color={
                              theme.highContrast.link ||
                              textColor ||
                              themeLinkColor
                            }
                            href={item.href}
                          >
                            {item.resource ? (
                              <FormattedMessage
                                id={item.resource}
                                defaultMessage={item.text}
                              />
                            ) : (
                              item.text
                            )}
                          </Link>
                        </ListItem>
                      ))}
                    </VerticalList>
                  )}
                </OverflowContainer>
              </ContentContainer>
              {showPlusButton && (
                <PlusButton
                  backgroundColor={
                    theme.highContrast.backgroundColor ||
                    backgroundColor ||
                    themeBgColor
                  }
                  size="60px"
                  position="absolute"
                  height="100%"
                  right={inverted ? "initial" : "-30px"}
                  left={inverted ? "-30px" : "initial"}
                  top="0"
                  zIndex="1"
                  as="a"
                  href={href}
                >
                  <Text
                    fontSize="50px"
                    fontWeight="100"
