export const ScrollableTab = withTheme(Tab);

const ScrollableTabs = ({ className, children, offSetClasses }) => {
  //Get Reference of TabsContainer
  const refTabsContainer = useRef(null);
  //Arrows States
  const [showLeftArrow, setLeftArrow] = useState(false);
  const [showRightArrow, setRightArrow] = useState(false);
  //What user can scroll
  const [scrollableWidth, setScrollableWidth] = useState(0);

  const checkHorizontalScroll = () => {
    let tabsScrollWidth = refTabsContainer.current.scrollWidth;
    let tabsClientWidth = refTabsContainer.current.clientWidth;
    //Update What user can scroll
    setScrollableWidth(tabsScrollWidth - tabsClientWidth);

    /*
    -- If tabsContainer has scroll
    -- Show rightArrow
    */
    if (tabsScrollWidth > tabsClientWidth) {
      setRightArrow(true);
    } else {
      setRightArrow(false);
    }
  };

  const checkScrollPosition = () => {
    //Update what user scrolled Left
    let scrolledLeftValue = refTabsContainer.current.scrollLeft;
    //Check if at end of container
    if (scrolledLeftValue === scrollableWidth) {
      setRightArrow(false);
      setLeftArrow(true);
    } else if (scrolledLeftValue === 0) {
      setRightArrow(true);
      setLeftArrow(false);
    }
  };

  const scrollRight = () => {
    refTabsContainer.current.scrollLeft += scrollableWidth;
  };

  const scrollLeft = () => {
    refTabsContainer.current.scrollLeft = 0;
  };

  useEffect(() => {
    window.addEventListener("resize", checkHorizontalScroll);
    checkHorizontalScroll();
    return () => {
      window.removeEventListener("resize", checkHorizontalScroll);
    };
  }, []);
  return (
    <div className={className}>
      <div className="container">
        <div className={String(offSetClasses)}>
          <div className="tabs" role="tablist">
            <ArrowButton
              display={showLeftArrow ? "block" : "none"}
              onClick={scrollLeft}
            />
            <ArrowButton
              arrowRight
              display={showRightArrow ? "block" : "none"}
              onClick={scrollRight}
            />
            <div
              className="tabs-container"
              ref={refTabsContainer}
              onScroll={checkScrollPosition}
            >
              {children}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

ScrollableTabs.defaultProps = {
  offSetClasses: "no-offset"
};

ScrollableTabs.propTypes = {
  className: PropTypes.string,
  children: PropTypes.node,
  offSetClasses: PropTypes.string
};

const ScrollableTabsWithStyles = styled(ScrollableTabs)`
  border-bottom: ${props => props.theme.components.scrollableTabs.border};
  margin-bottom: ${props => props.marginBottom || "50px"};

  .tabs {
    //For Buttons
    position: relative;

    .tabs-container {
      overflow-x: scroll;
      white-space: nowrap;
      scroll-snap-type: x mandatory;
      -webkit-overflow-scrolling: touch;
      scroll-behavior: smooth;

      //Hide ScrollBar
      &::-webkit-scrollbar {
        display: none;
        height: 0;
      }
      -ms-overflow-style: none;
      overflow: -moz-scrollbars-none;
      scrollbar-width: none;

      .tab {
        scroll-snap-align: start;
        display: inline-block;
        button {
          &:focus {
            ${props => props.theme.mixins.focus};
            outline-offset: -2px;
          }
        }
      }
      .tab:last-child {
        margin-right: 40px;
      }
    }
  }
`;

