import React, { Fragment, useState } from "react";
import PropTypes from "prop-types";
import styled, { withTheme } from "styled-components";
import {
  Icon,
  Link,
  Text,
  ClickAwayListener,
  HorizontalList,
  ListItem,
  VerticalList
} from "../../atoms";
import Dropdown from "../Dropdown";
import DropdownList from "../DropdownList";
import { rem } from "polished";
import { truncateOnWord } from "../../../utils/utilities";
import { useConfig } from "../../../context/ConfigContext";

const Crumb = ({
  text,
  theme,
  color,
  href,
  truncateLength,
  activeColor,
  type = "link"
}) => {
  return type === "text" ? (
    <Text
      size="article"
      color={color}
      display="inline-block"
      fontWeight={theme.components.breadcrumbs.link.fontWeight}
      text={truncateOnWord(
        text,
        truncateLength,
        true,
        text.length >= truncateLength ? "..." : ""
      )}
    />
  ) : (
    <Link
      href={href}
      color={color}
      fontSize={rem(theme.components.breadcrumbs.link.fontSize)}
      fontWeight={theme.components.breadcrumbs.link.fontWeight}
      textDecorationColor={theme.highContrast.link || activeColor}
      default
      display="inline-block"
      margin="6px 0"
    >
      {truncateOnWord(
        text,
        truncateLength,
        true,
        text.length >= truncateLength ? "..." : ""
      )}
    </Link>
  );
};

Crumb.defaultProps = {
  text: "link",
  activeColor: "#000"
};

Crumb.propTypes = {
  text: PropTypes.string,
  theme: PropTypes.object,
  color: PropTypes.string,
  href: PropTypes.string,
  truncateLength: PropTypes.number,
  activeColor: PropTypes.string,
  type: PropTypes.string
};

const More = ({ theme, onClick }) => {
  const { useIntl } = useConfig();
  const intl = useIntl();

  return (
    <button
      onClick={onClick}
      className="open-menu"
      aria-label={intl({
        id: "app.breadcrumbs.button.description",
        defaultMessage: "Breadcrumbs"
      })}
    >
      <Icon
        name="fal fa-ellipsis-h"
        color={theme.components.breadcrumbs.button.iconColor}
        size="xSmall"
      />
    </button>
  );
};

More.propTypes = {
  onClick: PropTypes.func,
  theme: PropTypes.object,
  ariaLabel: PropTypes.string
};

More.defaultProps = {
  ariaLabel: "Breadcrumbs"
};

const Breadcrumbs = ({
  className,
  theme,
  breadcrumbs,
  lang,
  anchors,
  truncateLength,
  anchorOnClick
}) => {
  const [onOpenAnchorMenu, setAnchorMenu] = useState(false);
  const [showMore, setShowMore] = useState(false);

  const limit = 4;
  const breadcrumbsTotal = breadcrumbs.length;
  const beforeItems =
    breadcrumbsTotal === 3 ? 2 : breadcrumbsTotal === 2 ? 1 : 3;

  const openAnchorMenu = () => {
    if (onOpenAnchorMenu) {
      setAnchorMenu(false);
    } else {
      setAnchorMenu(true);
    }
  };

  const onShowMoreBreadCrumbs = () => {
    setShowMore(true);
  };

  /*
  const checkIfUserScrolledDown = () => {
    let windowY = window.scrollY;
    if (windowY >= 40) {
      setAnchorMenu(false);
    }
  };

  useEffect(() => {
    window.addEventListener("scroll", checkIfUserScrolledDown);
    return () => {
      window.removeEventListener("scroll", checkIfUserScrolledDown);
    };
  }, []);
   */

  return (
    <div className={`${className} d-flex align-items-center`}>
      <HorizontalList display="inline-block">
        {breadcrumbs
          .filter((_, i) => i + 1 <= beforeItems)
          .map((item, index) => (
            <ListItem key={index}>
              <Crumb
                theme={theme}
                color={theme.components.breadcrumbs.link.color}
                text={item.text}
                href={`/${lang}${item.url}`}
                truncateLength={truncateLength}
                {...item}
              />
            </ListItem>
          ))}

        {breadcrumbs.length > limit && (
          <Fragment>
            <ClickAwayListener as="li" onChange={() => setShowMore(false)}>
              {!showMore && (
                <ListItem>
                  <More theme={theme} onClick={onShowMoreBreadCrumbs} />
                </ListItem>
              )}

              {showMore &&
                breadcrumbs
                  .filter(
                    (_, i) =>
                      i + 1 > beforeItems && breadcrumbs.length !== i + 1
                  )
                  .map((item, index) => (
                    <ListItem key={index}>
                      <Crumb
                        theme={theme}
                        color={theme.components.breadcrumbs.link.color}
                        text={item.text}
                        href={`/${lang}${item.url}`}
                        truncateLength={truncateLength}
                        {...item}
                      />
                    </ListItem>
                  ))}
            </ClickAwayListener>
          </Fragment>
        )}

        {breadcrumbs
          .filter((_, i) => breadcrumbs.length === i + 1)
          .map((item, index) => {
            return (
              <ListItem key={index}>
                <Fragment>
                  {anchors.length > 0 ? (
                    <ClickAwayListener onChange={() => setAnchorMenu(false)}>
                      <Dropdown
                        width="100%"
                        left="0"
                        display="block"
                        openMenuOnHover={false}
                      >
                        <button
                          onClick={openAnchorMenu}
                          className="open-anchor-menu d-flex align-items-center"
                        >
                          <Text
                            size="article"
                            color={
                              theme.components.breadcrumbs.anchors.textColor
                            }
                            text={truncateOnWord(
                              item.text,
                              truncateLength,
                              true,
                              item.text.length >= truncateLength ? "..." : ""
                            )}
                          />
                          <Icon
                            size="xSmall"
                            color={
                              theme.components.breadcrumbs.anchors.textColor
                            }
                            name={
                              onOpenAnchorMenu
                                ? theme.icons.chevron_up
                                : theme.icons.chevron_down
                            }
                            marginLeft="15px"
                          />
                        </button>
                        {onOpenAnchorMenu && (
                          <DropdownList fontWeight="400" hasMaxHeight={true}>
                            <VerticalList>
                              {anchors.map((item, i) => {
                                return (
                                  <ListItem
                                    key={i}
                                    className="dropdown-item"
                                    width="100%"
                                  >
                                    <button
                                      onClick={() => {
                                        setAnchorMenu(false);
                                        anchorOnClick(item);
                                      }}
                                    >
                                      {item.text}
                                    </button>
                                  </ListItem>
                                );
                              })}
                            </VerticalList>
                          </DropdownList>
                        )}
                      </Dropdown>
                    </ClickAwayListener>
                  ) : (
                    <Text
                      size="article"
                      color={theme.components.breadcrumbs.link.lastChild}
                      text={truncateOnWord(
                        item.text,
                        truncateLength,
                        true,
                        item.text.length >= truncateLength ? "..." : ""
                      )}
                    />
                  )}
                </Fragment>
              </ListItem>
            );
          })}
      </HorizontalList>
    </div>
  );
};

Breadcrumbs.defaultProps = {
  truncateLength: 30
};

Breadcrumbs.propTypes = {
  className: PropTypes.string,
  theme: PropTypes.object,
  breadcrumbs: PropTypes.array,
  lang: PropTypes.string,
  anchors: PropTypes.array,
  truncateLength: PropTypes.number,
  anchorOnClick: PropTypes.func
};

const BreadcrumbsWithTheme = withTheme(Breadcrumbs);

const BreadcrumbsWithStyles = styled(BreadcrumbsWithTheme)`
  min-height: ${props => props.theme.components.breadcrumbs.height};

  //Open menu button
  button.open-menu {
    width: 32px;
    height: 32px;
    background-color: ${props =>
      props.theme.components.breadcrumbs.button.bgColor};
    border: none;
    cursor: pointer;
    background-color: ${props =>
      props.theme.components.breadcrumbs.button.hoverBgColor};
  }

  //Breadcrumbs list items
  li:not(.dropdown-item) {
    color: ${props => props.theme.components.breadcrumbs.link.color};
    &:after {
      content: "/";
      font-weight: ${props =>
        props.theme.components.breadcrumbs.link.fontWeight};
      margin: 0 5px;
      font-size: ${props =>
        rem(props.theme.components.breadcrumbs.link.fontSize)};
    }
    &:last-child {
      &:after {
        content: initial;
      }
    }
  }

  //Anchors
  button.open-anchor-menu {
    background-color: ${props =>
      props.theme.components.breadcrumbs.anchors.buttonBgColor};
    border: none;
    padding: 0 10px;
    height: ${props => props.theme.components.breadcrumbs.height};
    cursor: pointer;
  }
`;

