import React, { useState, useEffect } from "react";
import PropTypes from "prop-types";
import styled, { withTheme } from "styled-components";
import { Icon, ClickAwayListener } from "../../atoms";
import ReactCalendar from "../ReactCalendar";
import moment from "moment";

const DatePicker = ({
  className,
  value,
  theme,
  calendarPrimaryColor,
  placeholder,
  onChange
}) => {
  const [isOpen, setOpen] = useState(false);
  const [inputValue, setInputValue] = useState(value);

  useEffect(() => {
    setInputValue(value);
  }, [value]);

  const onHandleChange = value => {
    const date = moment(value).format("DD-MM-YYYY");
    setInputValue(date);
    onChange(date);
    setOpen(false);
  };

  const onClear = () => {
    setInputValue("");
    onChange(null);
  };

  return (
    <ClickAwayListener onChange={() => setOpen(false)}>
      <div className={className}>
        <input
          type="text"
          value={inputValue}
          placeholder={placeholder || "dd/mm/aaaa"}
          onFocus={() => setOpen(true)}
          readOnly
        />
        {inputValue && (
          <div className="icon-container clear" onClick={onClear}>
            <Icon
              name={theme.icons.fas_times}
              color={theme.components.datePicker.iconColor}
              size="xSmall"
              aria-label="Abrir calendário"
            />
          </div>
        )}
        <div className="icon-container">
          <Icon
            name={theme.icons.calendar}
            color={theme.components.datePicker.iconColor}
            size="xSmall"
            aria-label="Abrir calendário"
          />
        </div>
        {isOpen && (
          <ReactCalendar
            top="45px"
            primaryColor={calendarPrimaryColor}
            onChange={onHandleChange}
          />
        )}
      </div>
    </ClickAwayListener>
  );
};

const types = {
  className: PropTypes.string,
  value: PropTypes.string,
  theme: PropTypes.object,
  calendarPrimaryColor: PropTypes.string,
  placeholder: PropTypes.string,
  onChange: PropTypes.func,
  showClear: PropTypes.bool
};

const defaults = {
  isOpen: false
};

DatePicker.propTypes = types;
DatePicker.defaultProps = defaults;

