const Agenda = memo(function Agenda({
  className,
  showHeader,
  title,
  resource,
  backgroundColor,
  eventStyles,
  hasError,
  href,
  loading,
  data,
  size,
  theme
}) {
  const { formattedMessage: FormattedMessage } = useConfig();
  const onRenderNews = (data, start = 0, end = 3) => {
    return data.listEvents
      .filter((items, index) => index >= start && index <= end && items)
      .map((item, index) => {
        const isSameDay = moment(item.startDate).isSame(item.endDate, "day");

        const highContrastBgColor = theme.highContrast.backgroundColor;
        const agendaCardTextColor = theme.components.agenda.agendaCardTextColor;
        const highContrastTextColor = theme.highContrast.text;
        const highContrastActionColor = theme.highContrast.action;
        return (
          <AgendaCard
            key={index}
            textColorHover={highContrastBgColor || backgroundColor}
            iconColorHover={highContrastBgColor || backgroundColor}
            squareColorHover={
              highContrastActionColor ||
              theme.components.agenda.squareColorHover
            }
            buttonOutlineColorHover={highContrastBgColor || backgroundColor}
            href={item.url}
