                    color={highContrastTextColor || eventStyles.date.textColor}
                    fontWeight="900"
                    lineHeight="1"
                  />
                  <Text
                    fontSize="18px"
                    text={moment(item.endDate, "YYYY/MM/DD").format("MMM")}
