  const AgendaFeaturedColumn = () => {
    const agendaFeaturedTextColor =
      theme.components.agenda.agendaFeaturedTextColor;
    const highContrastBgColor = theme.highContrast.backgroundColor;
    return (
      <div className="col-xl-6 col-lg-6 col-md-12 col-12 order-lg-last order-first">
