              plusButtonHoverBgColor={highContrastBgColor || backgroundColor}
              textColorHover={agendaFeaturedTextColor}
              buttonTextColor={agendaFeaturedTextColor}
              textColor={agendaFeaturedTextColor}
              titleColor={agendaFeaturedTextColor}
              borderColor="#fff"
              dateContainerDay={moment(
                data.listEventsHighlight[0].startDate,
                "YYYY/MM/DD"
              ).format("DD")}
              dateContainerMonth={moment(
                data.listEventsHighlight[0].startDate,
                "YYYY/MM/DD"
              ).format("MMM")}
              //Aria-Label
              dateContainerMonthAriaLabel={moment(
                data.listEventsHighlight[0].startDate,
                "YYYY/MM/DD"
              ).format("MMMM")}
              text={data.listEventsHighlight[0].local}
              title={data.listEventsHighlight[0].title}
              date={
                moment(data.listEventsHighlight[0].startDate).isSame(
                  data.listEventsHighlight[0].endDate,
                  "day"
                )
                  ? `${moment(data.listEventsHighlight[0].startDate).format(
                      "DD MMMM YYYY"
                    )}`
                  : `${moment(data.listEventsHighlight[0].startDate).format(
                      "DD MMMM"
                    )} - ${moment(data.listEventsHighlight[0].endDate).format(
                      "DD MMMM YYYY"
                    )}`
              }
            />
          </ContentContainer>
        ) : (
          onRenderNews(data, 4, 8)
        )}
      </div>
    );
  };
  // TODO Corrigir endereço href no json
  return (
    <Section
      className={className}
      backgroundColor={theme.highContrast.backgroundColor || backgroundColor}
    >
      {showHeader ? (
        <SectionHeader
          textColor={theme.components.agenda.titleColor}
          href={href}
        >
          {resource ? (
            <FormattedMessage id={resource} defaultMessage={title} />
