          <Error />
        ) : (
          <Fragment>
            <div className="row">
              {/*Change Columns order for design & screen reader*/}
              {size.width >= 992 ? (
                <Fragment>
                  <AgendaNewsColumn />
                  <AgendaFeaturedColumn />
                </Fragment>
              ) : (
                <Fragment>
                  <AgendaFeaturedColumn />
                  <AgendaNewsColumn />
                </Fragment>
              )}
            </div>
          </Fragment>
        )}
      </div>
    </Section>
  );
});

