import React, { memo, useRef } from "react";
import PropTypes from "prop-types";
//Styles
import styled, { withTheme } from "styled-components";
//Components
import Slider from "react-slick";
import Loading from "./Loading";
import { ScreenReaderText, Title, Section } from "../../atoms";
import {
  CustomArrow,
  SectionHeader,
  NewsCard,
  Error,
  PersonCard
} from "../../molecules";
import { withSize } from "react-sizeme";
import { compose } from "../../../utils/utilities";
//Context
import { useConfig } from "../../../context/ConfigContext";

const News = memo(function News({
  className,
  showHeader,
  title,
  resource,
  textColor,
  backgroundColor,
  data,
  loading,
  hasError,
  cardStyles,
  titleProps,
  onAction,
  onEnter,
  size,
  theme,
  arrowColor,
  ...props
}) {
  const sliderRef = useRef(null);
  const { formattedMessage: FormattedMessage } = useConfig();

  const onHandleTabNavigation = e => {
    if (e.key === "Tab" && e.shiftKey) {
      sliderRef.current.slickPrev();
    }

    if (e.key === "Tab") {
      sliderRef.current.slickNext();
    }
  };

  return (
    <Section
      className={className}
      backgroundColor={theme.highContrast.backgroundColor || backgroundColor}
    >
      <ScreenReaderText>
        <Title text="Notícias" />
      </ScreenReaderText>
      {showHeader && (
        <SectionHeader
          textColor={theme.highContrast.link || textColor}
          {...titleProps}
          href={props.categoryUrl}
        >
          {resource ? (
            <FormattedMessage id={resource} defaultMessage={title} />
          ) : (
            title
          )}
        </SectionHeader>
      )}
      <div className="container news-container">
        {loading ? (
          <Loading />
        ) : hasError ? (
          <Error />
        ) : (
          <Slider
            ref={sliderRef}
            useTransform={false}
            slidesToShow={3}
            slidesToScroll={1}
            speed={500}
            infinite={false}
            centerMode={false}
            centerPadding={0}
            dots={false}
            initialSlide={0}
            customPaging={i => {
              return <button aria-label={`Notícia ${i + 1}`}>&nbsp;</button>;
            }}
            prevArrow={
              <CustomArrow
                ariaLabel="slide anterior"
                color={
                  arrowColor || theme.components.customArrow.customArrowColor
                }
                left
                darken
              />
            }
            nextArrow={
              <CustomArrow
                ariaLabel="próximo slide"
                color={
                  arrowColor || theme.components.customArrow.customArrowColor
                }
                darken
              />
            }
            responsive={[
              {
                breakpoint: 1199,
                settings: {
                  slidesToShow: 3
                }
              },
              {
                breakpoint: 991,
                settings: {
                  slidesToShow: 2
                }
              },
              {
                breakpoint: 767,
                settings: {
                  slidesToShow: 1
                }
              },
              {
                breakpoint: 575,
                settings: {
                  slidesToShow: 1,
                  arrows: false,
                  dots: true
                }
              }
            ]}
          >
            {data.listNews.map(item => {
              if (item.style === 1 || item.style === 2) {
                let hasImageCover = item.imageCover !== null;
                let imageSrc =
                  hasImageCover && item.imageCover.url !== null
                    ? item.imageCover.url
                    : "";

                let imageAlt =
                  hasImageCover && item.imageCover.alt !== null
                    ? item.imageCover.alt
                    : "";
                return (
                  <div key={item.id} className="col">
                    <PersonCard
                      title={item.title}
                      name={item.pretitle}
                      src={imageSrc}
                      url={item.url}
                      alt={imageAlt}
                      titleIsSerif={true}
                      size={size}
                      share={item.share}
                      history={props.history}
                      backgroundColor={
                        item.style === 2
                          ? theme.components.news.personCard.style2BgColor
                          : ""
                      }
                      textColor={
                        item.style === 2
                          ? theme.components.news.personCard.style2Color
                          : ""
                      }
                      minHeight="398px"
                    />
                  </div>
                );
              } else {
                return (
                  <NewsCard
                    key={item.id}
                    data={item}
                    onClick={e => onAction(e, item.url)}
                    onKeyPress={e => onEnter(e, item.url)}
                    hasCategoryTitle={props.categoryId !== item.categoryID}
                    cardBgColor={
                      theme.highContrast.lightBackgroundColor ||
                      cardStyles.backgroundColor
                    }
                    imageBgColor={cardStyles.backgroundColor}
                    linkColor={cardStyles.textColor}
                    onKeyDown={onHandleTabNavigation}
                    history={props.history}
                    hasShareIcon={props.hasShareIcon}
                    size={size}
                  />
                );
              }
            })}
          </Slider>
        )}
      </div>
    </Section>
  );
});

const defaults = {
