    background-color: ${props =>
      props.theme.components.reactSlickSlider.buttonBgColor};
    width: 8px;
    height: 8px;
  }

  // Large devices (desktops, 992px and up to 1199)
  @media (min-width: 992px) and (max-width: 1199px) {
    .slick-prev {
      left: -15px;
    }
    .slick-next {
      right: -15px;
    }
  }

  //Full height slider with flexBox
  ${props =>
    props.hasFullHeightSlider &&
    `
    .slick-list,
    .slick-track {
      display: flex;
    }

    .slick-slide {
      display: flex;
      flex-direction: column;

      > div {
        display: flex;
