import React, { Fragment, useMemo } from "react";
import PropTypes from "prop-types";
//components
import Loading from "./Loading";
import { Section } from "../../atoms";
import { SectionHeader, Error } from "../../molecules";
import TwoCols from "./TwoCols";
import OneCol from "./OneCol";
import NestedRow from "./NestedRow";
import { withTheme } from "styled-components";

const Modifier = (variant, props, data) => {
  switch (variant) {
    case "TwoCols":
      return <TwoCols {...props} data={data} />;
    case "OneCol":
      return <OneCol {...props} data={data} />;
    case "NestedRow":
      return <NestedRow {...props} data={data} />;
    default:
      return <OneCol {...props} data={data} />;
  }
};

const Contents = ({
  title: titleProp,
  textColor,
  variant,
  hasTitle,
  hasLine,
  loading,
  content,
  hasError,
  lang,
  theme,
  ...props
}) => {
  const title = useMemo(() => titleProp[lang] || titleProp, [titleProp, lang]);

  //Removes the component if there is not content
  if (!loading && content.data !== undefined) {
    if (
      variant === "TwoCols" &&
      content.data.description &&
      content.data.description.length <= 0
    )
      return null;

    if (
      variant === "NestedRow" &&
      content.data.contents[0].list.length <= 0 &&
      content.data.contents[1].list.length <= 0
    ) {
      return null;
    }
  }

  return (
    <Section
      backgroundColor={
        theme.highContrast.backgroundColor || props.backgroundColor
      }
    >
      {loading ? (
        <Loading
          backgroundColor={
            theme.highContrast.backgroundColor || props.backgroundColor
          }
        />
      ) : hasError ? (
        <Error />
      ) : (
        <Fragment>
          {hasTitle && (
            <SectionHeader
              title={title}
              textColor={theme.highContrast.text || textColor}
              hasLine={hasLine}
              hasLink={hasLine}
            />
          )}

          <div className="container">
            {Modifier(variant, props, content.data)}
          </div>
        </Fragment>
      )}
    </Section>
  );
};

