                  tag="h1"
                  text={content.title}
                  color={theme.highContrast.text || titleColor}
                  marginBottom="10px"
                  firstLetter="capitalize"
                />
                {hasLine && (
                  <Line
                    color={theme.components.courses.lineColor}
                    marginBottom="25px"
                  />
                )}
                <ContentContainer mobileMdDisplay="none">
                  <Text
                    text={content.description}
                    color={theme.highContrast.text || textColor}
