                              onChange={option =>
                                onFilter(option, item.jsonField)
                              }
                              isClearable
                            />
                          </ContentContainer>
                        </div>
                      );
                    })}
                  </div>
                </ContentContainer>

                <ContentContainer
                  marginTop="40px"
                  marginRight="0"
