                                  }
                                  hasHover={res.columns[3].href && true}
                                  aria-label={
                                    res.columns[3].href
                                      ? res.columns[3].href
                                      : intl({
                                          id:
                                            "app.course.edicts.icon.empty.documents",
                                          defaultMessage:
                                            "Ausência de documentos"
                                        })
                                  }
                                  native
                                >
                                  ver ata
                                </Link>
                              </ListItem>
                              <ListItem paddingLeft="5px" lineHeight="1">
                                <Link
                                  as={res.columns[2].href ? "a" : "span"}
                                  fontWeight="300"
                                  display="inline-block"
                                  textDecoration="underline"
                                  target={res.columns[2].href ? "_blank" : null}
                                  href={
                                    res.columns[2].href
                                      ? res.columns[2].href
                                      : null
                                  }
                                  color={
                                    res.columns[2].href
                                      ? theme.components.edicts.iconColor
                                      : theme.components.edicts.emptyIconColor
                                  }
                                  cursor={
                                    res.columns[2].href ? "pointer" : "default"
                                  }
                                  hasHover={res.columns[2].href && true}
                                  aria-label={
                                    res.columns[2].href
                                      ? intl({
                                          id:
                                            "app.course.edicts.icon.documents",
                                          defaultMessage:
                                            "Aceder aos documentos"
                                        })
                                      : intl({
                                          id:
                                            "app.course.edicts.icon.empty.documents",
                                          defaultMessage:
                                            "Ausência de documentos"
                                        })
                                  }
                                  native
                                >
                                  ver edital
                                </Link>
                              </ListItem>
                            </HorizontalList>
                          </ContentContainer>
                        </BorderedCard>
                      );
                    })
                ) : (
                  <Table
                    marginTop="0"
                    col2Size="230px"
                    col3Size="65px"
                    col4Size="30px"
                    col2SizeMobile="145px"
                    col3SizeMobile="65px"
                    col4SizeMobile="30px"
                    tableRowSpace="10px"
                    borders
                  >
                    {Object.keys(tables).length <= 1 ? (
                      <Error>
                        Não existe informação para esta fase{" "}
                        <b>
                          {faseFilterValue.label || degreeFilterValue.label}
                        </b>
                      </Error>
                    ) : (
                      <Fragment>
                        {tables
                          .filter(item => item.isHeader)
                          .map((item, index) => {
                            return (
                              <thead key={`table-${item.fase}-${index}`}>
                                <tr>
                                  {item.columns.map((cols, index) => {
                                    return (
                                      <th key={index}>
                                        <Text fontSize="14px" fontWeight="400">
                                          {cols.text}
                                        </Text>
                                      </th>
                                    );
                                  })}
                                </tr>
                              </thead>
                            );
                          })}

                        <tbody>
                          {tables
                            .filter(item => !item.isHeader)
                            .map((item, index) => {
                              return (
                                <tr key={index}>
                                  {item.columns.map(cols => {
                                    return cols.type === "link" ? (
                                      <td>
                                        <Link
                                          color={
                                            theme.components.edicts.linkColor
                                          }
                                          fontSize="14px"
                                          fontWeight="300"
                                          href={`${hasHash}/${lang}/curso/${cols.id}`}
                                          aria-label={cols.text}
                                        >
                                          {cols.text}
                                        </Link>
                                      </td>
                                    ) : cols.type === "icon" ? (
                                      <td>
                                        <Link
                                          as={cols.href ? "a" : "span"}
                                          href={cols.href ? cols.href : null}
                                          aria-label={
                                            cols.href
                                              ? intl({
                                                  id:
                                                    "app.course.edicts.icon.documents",
                                                  defaultMessage:
                                                    "Aceder aos documentos"
                                                })
                                              : intl({
                                                  id:
                                                    "app.course.edicts.icon.empty.documents",
                                                  defaultMessage:
                                                    "Ausência de documentos"
                                                })
                                          }
                                          target={cols.href && "_blank"}
                                          hasHover={cols.href}
                                          cursor={
                                            cols.href ? "pointer" : "default"
                                          }
                                          native
                                        >
                                          <Icon
                                            name="fal fa-file"
                                            color={
                                              cols.href === ""
                                                ? theme.components.edicts
                                                    .emptyIconColor
                                                : theme.components.edicts
                                                    .iconColor
                                            }
                                            fontSize="14px"
                                          />
                                        </Link>
                                      </td>
                                    ) : (
                                      <td>
                                        <Text fontSize="14px" fontWeight="300">
                                          {cols.text}
                                        </Text>
                                      </td>
                                    );
                                  })}
                                </tr>
                              );
                            })}
                        </tbody>
                      </Fragment>
                    )}
                  </Table>
                )}
              </Fragment>
            )}
          </div>
        </div>
      </div>
    </Section>
  );
};

