//Components
import Loading from "./Loading";
import {
  Title,
  Line,
  Text,
  Section,
  ContentContainer,
  VerticalList,
  ListItem
} from "../../atoms";
//import { StoreContext } from "../../../context/StoreContext";
import { withTheme } from "styled-components";
//Context
import { useConfig } from "../../../context/ConfigContext";

const ContentList = ({
  title,
  resource,
  loading,
  hasError,
  data,
  titleColor,
  className,
  hasLine,
  lang,
  theme,
  activeColor
}) => {
  const { link: Link, formattedMessage: FormattedMessage } = useConfig();
  /*
  const GlobalStore = useContext(StoreContext);

  const {
    state: { activeColor }
  } = GlobalStore;
  */

  return (
    <Section
      hasHighContrastBorderTop={false}
      className={className}
      backgroundColor={theme.components.contentList.backgroundColor}
    >
      {/** <SEO title={title} /> */}
      <div className="container">
        {loading ? (
          <Loading />
        ) : hasError ? (
          <div>No content to display</div>
        ) : (
          <div className="row no-gutters">
            <div className="offset-xl-3 col-xl-7 offset-lg-4 col-lg col-12">
              <Title
                tag="h1"
                color={theme.highContrast.text || titleColor}
                marginBottom="10px"
                firstLetter="capitalize"
              >
                {resource ? (
                  <FormattedMessage id={resource} defaultMessage={title} />
                ) : (
                  title
                )}
              </Title>
              {hasLine && (
                <Line
                  color={theme.components.contentList.lineColor}
                  marginBottom="25px"
                />
              )}
              <ContentContainer
                marginTop="40px"
                marginRight="0"
                mobileXsMarginRight="0"
              >
                {data.list.map((res, index) => {
                  return (
                    <VerticalList key={index}>
                      <ListItem
                        paddingTop="10px"
                        paddingBottom="10px"
                        borderBottom={`1px solid ${theme.components.contentList.borderColor}`}
                        marginTop={index !== 0 ? "40px" : "0"}
                      >
                        <Text
                          aria-label={res.title}
                          color={theme.components.contentList.listTitleColor}
                          fontSize="14px"
                          fontWeight="400"
                        >
                          {res.title}
                        </Text>
                      </ListItem>

                      {res.list.map((item, index) => {
                        let url =
                          lang === "pt"
                            ? `cursos/${item.type}/${item.value}`
                            : `courses/${item.type}/${item.value}`;
                        let slugUrl = `cursos/tipo/${item.slug}`;
                        let hasSlug = item.slug !== undefined || item.slug;

                        return (
                          <ListItem
                            key={index}
                            paddingTop="10px"
                            paddingBottom="10px"
                            borderBottom={`1px solid ${theme.components.contentList.borderColor}`}
                          >
                            <Link
                              href={{
                                pathname: hasSlug ? slugUrl : url,
                                state: {
                                  slug: hasSlug,
                                  type: item.type,
                                  value: item.value
                                }
                              }}
                              aria-label={item.label}
                              fontSize="14px"
                              fontWeight="300"
                              default={true}
                              color={theme.components.contentList.listLinkColor}
                              textDecorationColor={
                                theme.highContrast.linkHover || activeColor
                              }
                            >
                              {item.label}{" "}
                              {item.note && (
                                <Text
                                  as="span"
                                  size="small"
                                  color={theme.components.courses.noteColor}
                                >
                                  {item.note}
                                </Text>
                              )}
                            </Link>
                          </ListItem>
                        );
                      })}
                    </VerticalList>
                  );
                })}
              </ContentContainer>
            </div>
          </div>
        )}
      </div>
    </Section>
  );
};

