import React, { Fragment } from "react";
import PropTypes from "prop-types";
//Components
import { Text, Line, ContentContainer, Section } from "../../atoms/index";
import Loading from "./Loading";
import { Select, Table, Indent } from "../../molecules";
//import { StoreContext } from "../../../context/StoreContext";
import { withTheme } from "styled-components";
//Context
import { useConfig } from "../../../context/ConfigContext";

function CurricularPlan({
  className,
  hasLine,
  isLoading,
  hasError,
  content,
  tables,
  filters,
  hasHash,
  onFilter,
  theme,
  lang,
  activeColor
}) {
  const { link: Link } = useConfig();
  /*
  const GlobalStore = useContext(StoreContext);

  const {
    state: { activeColor }
  } = GlobalStore;
   */
  return (
    <Section
      className={className}
      backgroundColor={theme.components.curricularPlan.backgroundColor}
      hasHighContrastBorderTop={false}
    >
      <div className="container">
        <div className="row no-gutters align-items-center">
          <div className="col-xl-8 offset-xl-3 col-lg-8 offset-lg-4 col-12 offset-0">
            {isLoading ? (
              <Loading />
            ) : hasError ? (
              <Loading />
            ) : (
              <Fragment>
                <Text
                  as="h2"
                  size="xLarge"
                  text={content.title}
                  marginBottom="10px"
                  lineHeight="1.4"
                  firstLetter="capitalize"
                  fontWeight="500"
                />
                {hasLine && (
                  <Line
                    color={theme.components.curricularPlan.lineColor}
                    marginBottom="5px"
                  />
                )}
                {filters.map((item, index) => {
                  if (Object.keys(item.options).length > 0) {
                    return (
                      <ContentContainer
                        key={index}
                        marginTop="40px"
                        marginBottom="40px"
                      >
                        <Select
                          variant="black"
                          options={item.options}
                          placeholder={item.title}
                          label={item.title}
                          onChange={onFilter}
                          isClearable
                        />
                      </ContentContainer>
                    );
                  }
                  return true;
                })}
                {tables.map((item, index) => {
                  return (
                    <Table
                      key={index}
                      col2Size="150px"
                      col3Size="100px"
                      col2SizeMobile="120px"
                      col3SizeMobile="65px"
                      activeColor={activeColor}
                    >
                      <caption>{item.tableTitle}</caption>

                      {item.table.map((items, tableIndex) => {
                        if (items.isHeader) {
                          return (
                            <thead>
                              <tr>
                                {items.columns.map((col, index) => {
                                  return (
                                    <th key={index}>
                                      <Text
                                        size="article"
                                        lineHeight="1.6"
                                        fontWeight="400"
                                      >
                                        {col.text}
                                      </Text>
                                    </th>
                                  );
                                })}
                              </tr>
                            </thead>
                          );
                        } else {
                          return (
                            <tbody>
                              <tr>
                                {items.columns.map(col => {
                                  if (col.indent) {
                                    return (
                                      <td>
                                        <Indent double>
                                          <Link
                                            fontSize="15px"
                                            lineHeight="1.6"
                                            fontWeight="300"
                                            aria-label={col.text}
                                            href={`${hasHash}/${lang}/uc/${col.id}`}
                                            default={true}
                                            color={
                                              theme.components.curricularPlan
                                                .linkColor
                                            }
                                            textDecorationColor={
                                              theme.highContrast.link ||
                                              activeColor
                                            }
                                          >
                                            {col.text}
                                          </Link>
                                        </Indent>
                                      </td>
                                    );
                                  }
                                  if (
                                    Object.keys(item.table).length ===
                                    tableIndex + 1
                                  ) {
                                    return (
                                      <td>
                                        <Text
                                          className="last-child"
                                          size="article"
                                          lineHeight="1.6"
                                          fontWeight="400"
                                        >
                                          {col.text}
                                        </Text>
                                      </td>
                                    );
                                  } else {
                                    return (
                                      <td>
                                        {col.type === "text" ? (
                                          <Text
                                            className={`${
                                              col.isOption ? "option" : ""
                                            } ${col.isMinor ? "minor" : ""} ${
                                              col.doubleIdent
                                                ? "option-inside-minor"
                                                : ""
                                            } `}
                                            size="article"
                                            lineHeight="1.6"
                                            fontWeight="300"
                                            title={col.title}
                                          >
                                            {col.text}
                                          </Text>
                                        ) : (
                                          <Link
                                            className={`${
                                              col.isOption ? "option" : ""
                                            } ${col.isMinor ? "minor" : ""} ${
                                              col.doubleIdent
                                                ? "option-inside-minor"
                                                : ""
                                            } `}
                                            href={`${hasHash}/${lang}/uc/${col.id}`}
                                            fontSize="15px"
                                            lineHeight="1.6"
                                            fontWeight="300"
                                            aria-label={col.text}
                                            default={true}
                                            color={
                                              theme.components.curricularPlan
                                                .linkColor
                                            }
                                            textDecorationColor={
                                              theme.highContrast.link ||
                                              activeColor
                                            }
                                          >
                                            {col.text}
                                          </Link>
                                        )}
                                      </td>
                                    );
                                  }
                                })}
                              </tr>
                            </tbody>
                          );
                        }
                      })}
                    </Table>
                  );
                })}
              </Fragment>
            )}
          </div>
        </div>
      </div>
    </Section>
  );
}

