import React, { Fragment, memo } from "react";
import PropTypes from "prop-types";
//Styles
import styled, { withTheme } from "styled-components";
//components
import Slider from "react-slick";
import { Text, Button, Section, ContentContainer } from "../../atoms";
import { SectionHeader, CustomArrow, MarkdownRenderer } from "../../molecules";
//Context
import { useConfig } from "../../../context/ConfigContext";

const Cards = memo(function Cards({
  title,
  textColor,
  titleColor,
  backgroundColor,
  className,
  hasLine,
  slidesToShow,
  content,
  activeColor,
  state,
  theme
}) {
  const { formattedMessage: FormattedMessage } = useConfig();
  return (
    <Section
      className={className}
      backgroundColor={
        theme.highContrast.backgroundColor ||
        backgroundColor ||
        theme.components.cards.backgroundColor
      }
    >
      <Fragment>
        {title && (
          <SectionHeader
            marginBottom="30px"
            title={title}
            textColor={titleColor || theme.components.cards.titleColor}
            hasLine={hasLine}
            hasLink={hasLine}
          />
        )}
        <div className="container candidature-container">
          <Slider
            useTransform={false}
            slidesToShow={slidesToShow}
            slidesToScroll={1}
            speed={500}
            infinite={false}
            centerMode={false}
            centerPadding={0}
            dots={false}
            arrows={content.length >= slidesToShow}
            initialSlide={0}
            customPaging={i => {
              return <button aria-label={`${i + 1}ª Fase`}>&nbsp;</button>;
            }}
            prevArrow={
              <CustomArrow
                color={theme.components.cards.arrowColor}
                left
                darken
              />
            }
            nextArrow={
              <CustomArrow color={theme.components.cards.arrowColor} darken />
            }
            responsive={[
              {
                breakpoint: 1199,
                settings: {
                  slidesToShow: 3
                }
              },
              {
                breakpoint: 991,
                settings: {
                  slidesToShow: 2
                }
              },
              {
                breakpoint: 767,
                settings: {
                  slidesToShow: 1
                }
              },
              {
                breakpoint: 575,
                settings: {
                  slidesToShow: 1,
                  arrows: false,
                  dots: true
                }
              }
            ]}
          >
            {content.map((card, index) => {
              return (
                <ContentContainer
                  backgroundColor={
                    theme.highContrast.lightBackgroundColor || "inherit"
                  }
                  position="relative"
                  height="inherit"
                  key={index}
                >
                  <ContentContainer
                    height="inherit"
                    padding="20px"
                    display="flex"
                    flexDirection="column"
                    justifyContent="space-between"
                    tabIndex={0}
                  >
                    <div>
                      {card.title && (
                        <Text
                          as="h3"
                          text={card.title}
                          size="mediumSmall"
                          fontWeight="500"
                          color={theme.highContrast.text || textColor}
                        />
                      )}
                      <ContentContainer marginTop="20px" color={textColor}>
                        <MarkdownRenderer
                          content={card.content}
                          activeColor={activeColor || state.activeColor}
                        />
                      </ContentContainer>
                    </div>
                    {card.href !== null && (
                      <ContentContainer marginTop="30px">
                        <Button
                          as="a"
                          variant="default"
                          className="card-button"
                          display="inline-flex"
                          width="fit-content"
                          textDecoration="none"
                          href={card.href}
                          target={card.externalLink ? "_blank" : "_self"}
                          native={card.externalLink}
                          backgroundColor={
                            theme.highContrast.lightBackgroundColor
                          }
                        >
                          {card.buttonTitle || (
                            <FormattedMessage
                              id="app.organism.button.knowMore"
                              defaultMessage="saber mais"
                            />
                          )}
                        </Button>
                      </ContentContainer>
                    )}
                  </ContentContainer>
                </ContentContainer>
              );
            })}
          </Slider>
        </div>
      </Fragment>
    </Section>
  );
});

const defaults = {
