  .slick-dots {
    width: 100%;
    bottom: 0;
    position: relative;
    margin-top: 10px;
    text-align: right;
    padding: 0 15px;
  }

  .slick-dots li {
    margin: 0 5px;
    &:last-child {
      margin-right: 0;
    }
  }

  .slick-dots li,
  .slick-dots li button {
    width: 8px;
