import React, { memo, Fragment } from "react";
import PropTypes from "prop-types";
import styled, { withTheme } from "styled-components";
//Components
import Loading, { LoadingAvatar, LoadingTable } from "./Loading";
import { Table, Error, MarkdownRenderer } from "../../molecules";
import {
  Image,
  Line,
  Icon,
  Link,
  Text,
  ContentContainer,
  ListItem,
  Section,
  HorizontalList
} from "../../atoms";
import moment from "moment";

const Profile = memo(function Profile({
  hasLine,
  showPhoto,
  pubsContent,
  photoContent,
  content,
  isLoadingPublications,
  isLoadingPerson,
  hasError,
  setShowPhoto,
  lang,
  theme,
  activeColor,
  externalContent,
  externalPubsContent
}) {
  const computeIsColumn = (item, index) => {
    return index > 3 || item.label === "Atribuições funcionais";
  };

  const onRenderTable = data => {
    return data.map((res, index) => {
      return (
        <ContentContainer key={index} marginBottom="60px">
          <Text
            as="h3"
            text={res.title}
            fontWeight="400"
            color={theme.highContrast.text || activeColor}
            marginBottom="5px"
            size="medium"
            firstLetter="capitalize"
          />

          <Table
            marginTop="0"
            col2Size="30%"
            col3Size="30%"
            tableRowSpace="10px"
            lightBorders
          >
            <thead>
              <tr>
                {res.headers !== null ? (
                  res.headers.map(title => (
                    <th key={title}>
                      <Text fontSize="14px" fontWeight="400">
                        {title}
                      </Text>
                    </th>
                  ))
                ) : (
                  <th>
                    <Text fontSize="14px" fontWeight="400">
                      Título
                    </Text>
                  </th>
                )}
              </tr>
            </thead>
            <tbody>
              {res.type === "list" &&
                res.list.map((item, index) => {
                  return (
                    <tr key={index}>
                      <td>
                        <Link
                          fontSize="14px"
                          fontWeight="300"
                          color={theme.components.profile.linkColor}
                          href={item.extra}
                          hoverColor={theme.highContrast.link || activeColor}
                        >
                          {item.info.value}
                        </Link>
                      </td>
                    </tr>
                  );
                })}

              {res.type === "table" &&
                res.table.map((res, i) => {
                  return (
                    <tr key={i}>
                      {res.rows.map((item, index) => {
                        return (
                          <td key={index}>
                            {item.type === "link" && (
                              <Link
                                fontSize="14px"
                                fontWeight="300"
                                color={theme.components.profile.linkColor}
                                href={item.extra}
                                hoverColor={
                                  theme.highContrast.link || activeColor
                                }
                              >
                                {item.info.value}
                              </Link>
                            )}
                            {item.type === "text" && (
                              <Text
                                fontSize="14px"
                                fontWeight="300"
                                margin="0 5px 0 0"
                                display="inline-block"
                              >
                                {item.info.value}
                              </Text>
                            )}

                            {item.type === "date" && (
                              <Text
                                fontSize="14px"
                                fontWeight="300"
                                margin="0 5px 0 0"
                                display="inline-block"
                              >
                                {moment(item.info.value).format("DD/MM/YYYY")}
                              </Text>
                            )}
                          </td>
                        );
                      })}
                    </tr>
                  );
                })}
            </tbody>
          </Table>
        </ContentContainer>
      );
    });
  };

  if (isLoadingPerson)
    return (
      <Section
        backgroundColor={theme.components.profile.backgroundColor}
        hasHighContrastBorderTop={false}
      >
        <div className="container">
          <Loading theme={theme} />
        </div>
      </Section>
    );

  return (
    <Section
      backgroundColor={theme.components.profile.backgroundColor}
      hasHighContrastBorderTop={false}
    >
      <div className="container">
        {hasError ? (
          <Error />
        ) : (
          <Fragment>
            <div className="row no-gutters">
              <div className="col-xl-7 offset-xl-3 col-lg-8 offset-lg-4 col-12 offset-0">
                <ContentContainer marginBottom="40px">
                  <Text
                    as="h1"
                    size="xLarge"
                    text={content.name}
                    marginBottom="10px"
                    lineHeight="1"
                    firstLetter="capitalize"
                    fontWeight="500"
                  />
                  {hasLine && <Line color="#000" />}
                </ContentContainer>
              </div>
            </div>
            <div className="row no-gutters">
              <div className="col-xl-7 offset-xl-3 col-lg-8 offset-lg-4 col-12 offset-0 order-xl-1 order-2">
                {content.units &&
                  content.units.map(res => {
                    return (
                      <ContentContainer key={res.id} marginBottom="40px">
                        <Text
                          as="h3"
                          text={res.name}
                          color={theme.highContrast.text || activeColor}
                          fontWeight="400"
                          marginBottom="15px"
                          size="medium"
                        />
                        {res.info
                          .filter(item => item.value && item.value !== "")
                          .map((item, index) => {
                            return (
                              <ProfileItem
                                isColumn={computeIsColumn(item, index)}
                                key={index}
                              >
                                <Text
                                  key={index}
                                  text={`${item.label}: `}
                                  fontWeight="400"
                                  size="small"
                                  display="inline-block"
                                  className="profile-item-title"
                                  wordBreak="initial"
                                />
                                <MarkdownRenderer
                                  content={item.value}
                                  isInline={true}
                                  isProfile
                                />
                              </ProfileItem>
                            );
                          })}
                      </ContentContainer>
                    );
                  })}

                {content.biography && (
                  <ContentContainer marginBottom="40px">
                    <Text
                      text={content.biography.label}
                      color={theme.highContrast.text || activeColor}
                      fontWeight="400"
                      marginBottom="5px"
                      fontSize="14px"
                      as="h2"
                      firstLetter="capitalize"
                    />
                    <MarkdownRenderer content={content.biography.value} />
                  </ContentContainer>
                )}

                {content.extra !== undefined &&
                  Object.keys(content.extra).length > 0 &&
                  onRenderTable(content.extra)}

                {externalContent.loading ? (
                  <LoadingTable theme={theme} />
                ) : (
                  externalContent.data &&
                  externalContent.data.length > 0 &&
                  onRenderTable(externalContent.data)
                )}

                {isLoadingPublications ? (
                  <LoadingTable theme={theme} />
                ) : (
                  pubsContent.publications &&
                  pubsContent.publications.length > 0 &&
                  pubsContent.publications
                    .filter(
                      res => res.title !== null && res.publications.length > 0
                    )
                    .map((res, index) => {
                      return (
                        <ContentContainer key={index} marginBottom="60px">
                          <Text
                            as="h3"
                            text={res.title}
                            fontWeight="400"
                            color={theme.highContrast.text || activeColor}
                            marginBottom="5px"
                            size="medium"
                            firstLetter="capitalize"
                          />
                          <Table
                            marginTop="0"
                            col2Size="40%"
                            col3Size="45px"
                            tableRowSpace="10px"
                            lightBorders
                          >
                            <thead>
                              <tr>
                                <th>
                                  <Text fontSize="14px" fontWeight="400">
                                    título
                                  </Text>
                                </th>
                                <th>
                                  <Text fontSize="14px" fontWeight="400">
                                    autoria
                                  </Text>
                                </th>
                                <th>
                                  <Text fontSize="14px" fontWeight="400">
                                    ano
                                  </Text>
                                </th>
                              </tr>
                            </thead>
                            <tbody>
                              {res.publications.map((item, index) => {
                                return (
                                  <tr key={index}>
                                    <td>
                                      <Link
                                        fontSize="14px"
                                        fontWeight="300"
                                        color={
                                          theme.components.profile.linkColor
                                        }
                                        href={item.title.value}
                                        hoverColor={
                                          theme.highContrast.link || activeColor
                                        }
                                        target="_blank"
                                        native
                                        default
                                      >
                                        {item.title.label}
                                      </Link>
                                    </td>
                                    <td className="if-has-links">
                                      {item.authors.map((a, index) => {
                                        if (a.id === null) {
                                          return (
                                            <Text
                                              key={index}
                                              fontSize="14px"
                                              fontWeight="300"
                                              margin="0 5px 0 0"
                                              display="inline-block"
                                            >
                                              {a.name}&nbsp;
                                            </Text>
                                          );
                                        } else {
                                          return (
                                            <Link
                                              key={index}
                                              //href={`${hasHash}/${lang}/p/${a.id}`}
                                              href={`/${lang}/p/${a.id}`}
                                              fontSize="14px"
                                              fontWeight="300"
                                              color={
                                                theme.components.profile
                                                  .linkColor
                                              }
                                              margin="0 5px 0 0"
                                              hoverColor={
                                                theme.highContrast.link ||
                                                activeColor
                                              }
                                              default
                                            >
                                              {a.name}
                                            </Link>
                                          );
                                        }
                                      })}
                                    </td>
                                    <td>
                                      <Text fontSize="14px" fontWeight="300">
                                        {item.year}
                                      </Text>
                                    </td>
                                  </tr>
                                );
                              })}
                            </tbody>
                          </Table>
                        </ContentContainer>
                      );
                    })
                )}

                {externalPubsContent.loading ? (
                  <LoadingTable theme={theme} />
                ) : (
                  !externalPubsContent.error &&
                  externalPubsContent.data && (
                    <ContentContainer marginBottom="40px">
                      <Text
                        as="h3"
                        text={externalPubsContent.title}
                        fontWeight="400"
                        color={theme.highContrast.text || activeColor}
                        marginBottom="5px"
                        size="medium"
                        firstLetter="capitalize"
                      />

                      <MarkdownRenderer content={externalPubsContent.data} />
                    </ContentContainer>
                  )
                )}
              </div>
              <div className="col-xl-2 offset-xl-0 col-lg-2 offset-lg-4 col-md-3 col-5 order-xl-2 order-1">
                <ContentContainer
                  marginLeft="30px"
                  mobileLgMarginLeft="0"
                  lgMarginBottom="40px"
                >
                  {isLoadingPerson ? (
                    <LoadingAvatar theme={theme} />
                  ) : (
                    photoContent.image &&
                    showPhoto && (
                      <ContentContainer>
                        <Image
                          onError={() => setShowPhoto(false)}
                          width="100%"
                          src={photoContent.image.src}
                          alt={photoContent.name}
                        />
                      </ContentContainer>
                    )
                  )}

                  <ContentContainer marginTop="10px" marginBottom="5px">
                    {photoContent.image &&
                      photoContent.image.emails &&
                      photoContent.image.emails.map((item, index) => {
                        return (
                          <div key={index}>
                            <Link
                              text={item}
                              href={`mailto:${item}`}
                              target="_top"
                              textDecoration="underline"
                              fontSize="12px"
                              color={theme.components.profile.linkColor}
                              fontWeight="300"
                              hoverColor={
                                theme.highContrast.link || activeColor
                              }
                              lineHeight="1.6"
                              default
                              native
                            />
                          </div>
                        );
                      })}
                  </ContentContainer>
                  <ContentContainer>
                    <HorizontalList display="inline-block">
                      {photoContent.image &&
                        photoContent.image.links &&
                        photoContent.image.links.map((item, index) => {
                          return (
                            <ListItem
                              key={index}
                              marginTop="5px"
                              marginRight="5px"
                              verticalAlign="middle"
                            >
                              <Link
                                fontDecoration="none"
                                target="_blank"
                                href={item.url}
                                display="inherit"
                                height="22px"
                                width="22px"
                                background={
                                  item.hasIcon
                                    ? "transparent"
                                    : theme.highContrast.link
                                }
                                native
                              >
                                {item.hasIcon ? (
                                  <Icon
                                    name={`${item.icon}`}
                                    cursor="pointer"
                                    color={theme.components.profile.iconColor}
                                    fontSize="21px"
                                    title={item.title}
                                    darken
                                  />
                                ) : (
                                  <Image
                                    alt="logo do ORCID"
                                    height="21px"
                                    src={`/icons/${item.icon}`}
                                  />
                                )}
                              </Link>
                            </ListItem>
                          );
                        })}
                    </HorizontalList>
                  </ContentContainer>
                </ContentContainer>
              </div>
            </div>
          </Fragment>
        )}
      </div>
    </Section>
  );
});

const types = {
