import React, { Fragment } from "react";
import PropTypes from "prop-types";
//Styles
import styled, { withTheme, createGlobalStyle } from "styled-components";
//Components
import Slider from "react-slick";
import {
  Icon,
  ContentContainer,
  Text,
  AspectRatio,
  Picture
} from "../../atoms/index";
import { CustomArrow } from "../../molecules/index";
//utils
import { isEmpty } from "../../../utils/utilities";

const GlobalStyle = createGlobalStyle`
  body {
    overflow: hidden;
  }
`;

const SectionContainer = styled.section`
  z-index: 1000;
  display: flex;
  flex-direction: column;
  position: ${props => props.position};
  top: 0;
  right: 0;
  bottom: 0;
  left: 0;
  overflow-y: auto;
  padding: 25px 0;
  background-color: ${props =>
    props.theme.components.gallery.theater.backgroundColor};
  @media (max-width: 575.98px) {
    .container {
      padding-left: 0;
      padding-right: 0;
    }
  }
  &.fade-in {
    -webkit-animation: fade-in 0.3s cubic-bezier(0.39, 0.575, 0.565, 1) both;
    animation: fade-in 0.3s cubic-bezier(0.39, 0.575, 0.565, 1) both;
  }
  @keyframes fade-in {
    0% {
      opacity: 0;
    }
    100% {
      opacity: 1;
    }
  }
  &.fade-out {
    -webkit-animation: fade-out 0.3s ease-out both;
    animation: fade-out 0.3s ease-out both;
  }
  @keyframes fade-out {
    0% {
      opacity: 1;
    }
    100% {
      opacity: 0;
    }
  }
`;

const SliderContainer = styled.div`
  display: flex;
  align-items: center;
  flex-grow: 1;

  .slick-prev {
    left: -30px;
  }
  .slick-next {
    right: -30px;
  }
  .slick-next,
  .slick-prev {
    width: auto;
    height: 36px;
    top: calc(50% - 18px);
  }
  .slick-next:before,
  .slick-prev:before {
    content: none;
  }
`;

const IconButton = styled.button`
  cursor: pointer;
  border: none;
  background-color: transparent;
  height: 28px;
  width: 28px;
  &:focus {
    ${props => props.theme.mixins.focus};
  }
  margin-left: ${props => props.marginLeft};
`;

const Theater = ({
  className,
  theme,
  images,
  position,
  onClose,
  currentSlide
}) => {
  const textColor = theme.components.gallery.theater.textColor;
  const settings = {
    useTransform: false,
    slidesToShow: 1,
    slidesToScroll: 1,
    speed: 500,
    infinite: false,
    centerMode: false,
    centerPadding: 0,
    dots: false,
    initialSlide: 0
  };
  const onDownload = () => {
    if (typeof window !== "undefined") {
      let link = document.createElement("a");
      link.href = images[currentSlide].src;
      link.download = "Download.jpg";
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);
    }
  };
  return (
    <Fragment>
      <GlobalStyle />
      <SectionContainer className={className} position={position}>
        <div className="container">
          <ContentContainer
            className="d-flex justify-content-between"
            marginBottom="15px"
            mobileXsPadding="0 15px"
          >
            <IconButton onClick={onClose}>
              <Icon name="fal fa-times" size="medium" color={textColor} />
            </IconButton>
            <IconButton marginLeft="30px" onClick={onDownload}>
              <Icon
                name="fal fa-arrow-to-bottom"
                size="medium"
                color={textColor}
              />
            </IconButton>
          </ContentContainer>
        </div>
        <SliderContainer>
          <div className="container">
            <Slider
              {...settings}
              prevArrow={
                <CustomArrow
                  tabIndex="0"
                  arrowSize="medium"
                  ariaLabel="slide anterior"
                  color={textColor}
                  left
                  darken
                />
              }
              nextArrow={
                <CustomArrow
                  tabIndex="0"
                  arrowSize="medium"
                  ariaLabel="próximo slide"
                  color={textColor}
                  darken
                />
              }
              responsive={[
                {
                  breakpoint: 575,
                  settings: {
                    arrows: false
                  }
                }
              ]}
              initialSlide={currentSlide}
            >
              {images.map((item, i) => {
                return (
                  <Fragment key={i}>
                    <AspectRatio ratio="sixteenByNine">
                      <Picture
                        backgroundSize="cover"
                        src={item.src}
                        alt={item.alt}
                        height="100%"
                      />
                    </AspectRatio>
                    <ContentContainer marginTop="15px" mobileXsPadding="0 15px">
                      <Text color={textColor}>
                        <Text color={textColor} as="span" fontWeight="400">
                          {item.title}
                        </Text>{" "}
                        {isEmpty(item.description) ? "" : item.description}
                      </Text>
                    </ContentContainer>
                  </Fragment>
                );
              })}
            </Slider>
          </div>
        </SliderContainer>
      </SectionContainer>
    </Fragment>
  );
};

const defaults = {
  currentSlide: 0,
  position: "fixed",
  images: [
    {
      src: "https://static.ua.pt/images/ua/placeholder-bw.png",
      alt: "Imagem sem alt",
      title: "Title",
      description: "Text"
    }
  ],
  onClose: () => null
};

