export default Loading;
import React, { useState, useEffect } from "react";
import PropTypes from "prop-types";
//Styles
import styled, { withTheme, css } from "styled-components";
//Components
import { Section, Text, AspectRatio, Picture } from "../../atoms/index";
import { SectionHeader } from "../../molecules/index";
import Theater from "./theater";
import Loading from "./Loading";

import { MarkdownRenderer } from "../../molecules";
const DescriptionContainer = styled.div`
  @media (min-width: 768px) {
    width: 50%;
  }
  margin-bottom: 30px;
`;

const GridContainer = styled.div`
  display: -ms-grid;
  display: grid;
  align-items: start;
  -ms-grid-columns: (1fr) [ ${props => props.columns || 1}];
  grid-template-columns: repeat(${props => props.columns || 1}, 1fr);
  @media (max-width: 767.98px) {
    -ms-grid-columns: (1fr) [2];
    grid-template-columns: repeat(2, 1fr);
  }
  -ms-grid-rows: auto;
  grid-template-rows: auto;
  grid-gap: ${props => props.gridGap + "px" || "30px"};
`;

const ImageContainer = styled.div`
  ${props =>
    props.as === "button" &&
    css`
      width: 100%;
      cursor: pointer;
      border: none;
      background-color: transparent;
      text-align: left;
      &:focus {
        ${props => props.theme.mixins.focus};
      }
    `}
  ${props =>
    props.as === "a" &&
    css`
      text-decoration: none;
      &:focus {
        ${props => props.theme.mixins.focus};
      }
    `}
`;

const FallbackContainer = styled.div`
  [class^="col-"] {
    margin-bottom: 30px;
    &:nth-last-of-type(-n + 2) {
      margin-bottom: 0;
    }
  }
  img {
    object-fit: initial;
    width: auto;
    height: auto;
    max-width: 100%;
    max-height: 100%;
    margin: auto;
  }
`;

const Gallery = ({
  className,
  theme,
  title,
  description,
  hasDescription,
  images,
  hasLink,
  href,
  columns,
  hasTheater,
  isLoading,
  activeColor,
  isMobile
}) => {
  const [isOpen, setOpen] = useState(false);
  const [fadeIn, setFadeIn] = useState(false);
  const [currentSlide, setCurrentSlide] = useState(0);
  const [supportsGrid, setSupportsGrid] = useState(true);

  const onOpenTheater = (hasTheater, currentSlide) => {
    setCurrentSlide(currentSlide);
    setFadeIn(true);
    setOpen(hasTheater);
  };

  const onCloseTheater = () => {
    setFadeIn(false);
    setTimeout(() => {
      setOpen(false);
    }, 300);
  };

  useEffect(() => {
    const el = document.createElement("div");
    const supportsGrid = typeof el.style.grid === "string";
    setSupportsGrid(supportsGrid);
  }, []);

  return (
    <Section className={className}>
      <SectionHeader
        textColor={
          hasLink
            ? theme.highContrast.link
            : theme.components.gallery.titleColor
        }
        hasLink={hasLink}
        hasLine={hasLink}
        hasContainer={true}
        href={href}
      >
        {title}
      </SectionHeader>
      {!isLoading ? (
        <div className="container">
          {hasDescription && (
            <DescriptionContainer>
              <MarkdownRenderer
                content={description}
                activeColor={activeColor}
                isMobile={isMobile}
              />
            </DescriptionContainer>
          )}
          {supportsGrid ? (
            <GridContainer columns={columns} gridGap={20}>
              {images.map((item, i) => {
                return (
                  <ImageContainer
                    key={i}
                    as={
                      !item.hasLink && hasTheater
                        ? "button"
                        : item.hasLink
                        ? "a"
                        : "div"
                    }
                    onClick={() =>
                      onOpenTheater(!item.hasLink && hasTheater, i)
                    }
                    href={item.href}
                  >
                    <AspectRatio ratio="treeByTwo">
                      <Picture
                        backgroundSize="contain"
                        src={item.src}
                        alt={item.alt}
                        height="100%"
                      />
                    </AspectRatio>
                    {item.hasTitle && (
                      <Text marginTop="20px" size="article">
                        {item.title}
                      </Text>
                    )}
                  </ImageContainer>
                );
              })}
            </GridContainer>
          ) : (
            <FallbackContainer>
              <div className="row">
                {images.map((item, i) => {
                  return (
                    <div className="col-6" key={i}>
                      <ImageContainer
                        as={
                          !item.hasLink && hasTheater
                            ? "button"
                            : item.hasLink
                            ? "a"
                            : "div"
                        }
                        onClick={() =>
                          onOpenTheater(!item.hasLink && hasTheater, i)
                        }
                        href={item.href}
                      >
                        <AspectRatio ratio="treeByTwo">
                          <Picture
                            backgroundSize="contain"
                            src={item.src}
                            alt={item.alt}
                            height="100%"
                          />
                        </AspectRatio>
                        {item.hasTitle && (
                          <Text marginTop="20px" size="article">
                            {item.title}
                          </Text>
                        )}
                      </ImageContainer>
                    </div>
                  );
                })}
              </div>
            </FallbackContainer>
          )}
        </div>
      ) : (
        <Loading />
      )}
      {hasTheater && isOpen && (
        <Theater
          className={fadeIn ? "fade-in" : "fade-out"}
          onClose={onCloseTheater}
          images={images}
          currentSlide={currentSlide}
        />
      )}
    </Section>
  );
};

const defaults = {
  href: "#",
  hasTheater: false,
  hasLink: false,
  title: "Title",
  description: "Text",
