import React, { useRef } from "react";
import PropTypes from "prop-types";
//Styles
import styled, { withTheme } from "styled-components";
//Components
import Slider from "react-slick";
import { Section } from "../../atoms/index";
import {
  CustomArrow,
  SectionHeader,
  StudentCardLink
} from "../../molecules/index";

const Testimonials = ({
  className,
  title,
  textColor,
  data,
  showHeader,
  titleProps,
  backgroundColor,
  theme,
  isEditor
}) => {
  const sliderRef = useRef(null);

  const onHandleTabNavigation = e => {
    if (e.key === "Tab" && e.shiftKey) {
      sliderRef.current.slickPrev();
    }

    if (e.key === "Tab") {
      sliderRef.current.slickNext();
    }
  };

  return (
    <Section
      className={className}
      backgroundColor={
        theme.highContrast.backgroundColor ||
        backgroundColor ||
        theme.components.about.backgroundColor
      }
    >
      {showHeader ? (
        <SectionHeader
          textColor={theme.highContrast.text || textColor}
          {...titleProps}
        >
          {title}
        </SectionHeader>
      ) : null}
      <div className="container testimonials-container p-0">
        <Slider
          ref={sliderRef}
          tabIndex={-1}
          useTransform={false}
          slidesToShow={3}
          slidesToScroll={1}
          speed={500}
          infinite={false}
          centerMode={false}
          centerPadding={0}
          dots={false}
          initialSlide={0}
          customPaging={i => {
            return <button aria-label={`Testemunho ${i + 1}`}>&nbsp;</button>;
          }}
          prevArrow={
            <CustomArrow
              ariaLabel="slide anterior"
              color={theme.components.about.arrowColor}
              left
              darken
            />
          }
          nextArrow={
            <CustomArrow
              ariaLabel="próximo slide"
              color={theme.components.about.arrowColor}
              darken
            />
          }
          responsive={[
            {
              breakpoint: 1199,
              settings: {
                slidesToShow: 3
              }
            },
            {
              breakpoint: 991,
              settings: {
                slidesToShow: 2
              }
            },
            {
              breakpoint: 767,
              settings: {
                slidesToShow: 1
              }
            },
            {
              breakpoint: 575,
              settings: {
                slidesToShow: 1,
                arrows: false,
                dots: true
              }
            }
          ]}
        >
          {data.length > 0 &&
            data.map((item, index) => {
              return (
                <div className="content col-sm-12 col-xs-12 " key={index}>
                  <StudentCardLink
                    text={item.text}
                    src={item.person.image}
                    alt={item.person.alt}
                    name={item.person.name}
                    about={item.person.about}
                    imageBgColor={item.backgroundColor}
                    onKeyDown={onHandleTabNavigation}
                    href={item.cardUrl}
                    isEditor={isEditor}
                  />
                </div>
              );
            })}
        </Slider>
      </div>
    </Section>
  );
};

const defaults = {
  title: "Title",
  showHeader: true,
  data: [],
  isEditor: false
};

const types = {
  className: PropTypes.string,
  title: PropTypes.string,
