import React, { memo, Fragment, useState } from "react";
import PropTypes from "prop-types";
import moment from "moment";
//Styles
import styled, { withTheme } from "styled-components";
import { lighten } from "polished";
//Components
import { Text, Section, Button, ContentContainer } from "../../atoms/index";
import {
  InjectHtml,
  Select,
  Error,
  BorderedCard,
  GeneralAccordion,
  GeneralAccordionItem,
  GeneralAccordionItemTitle,
  GeneralAccordionItemBody,
  GeneralAccordionTitle,
  GeneralAccordionHeader
} from "../../molecules/";
import Loading from "./Loading";

import { compose } from "../../../utils/utilities";

const InjectHtmlRow = styled.div`
  margin-bottom: 20px;
`;
const Card = memo(function Card({
  data,
  session,
  highlightColor,
  theme,
  lang
}) {
  const buttonProps = {
    hoverColor: "#fff",
    color: highlightColor,
    hoverBackgroundColor: highlightColor,
    focusColor: "#fff",
    focusBackgroundColor: lighten(0.1, highlightColor),
    border: `1px solid ${highlightColor}`
  };
  moment.locale(`${lang}`);

  const isEmpty =
    (!data || data.length === 0) &&
    !session?.registrationLink &&
    !session?.startDate;

  if (isEmpty)
    return (
      <Error
        text={lang === "pt" ? "Sem Conteúdo" : "No Content"}
        className="col"
        minHeight={"100px"}
      />
    );

  return (
    <BorderedCard
      borderColor={theme.components.xperimenta.card.borderColor}
      contentContainerPadding="0"
      cardIsLink={false}
    >
      {data?.length > 0 && (
        <div className="row no-gutters">
          {data.map((item, index) => {
            let isLastChild = data?.length === index + 1;
            return (
              <InjectHtmlRow
                key={index}
                className={isLastChild ? "col-sm col-12" : "col"}
              >
                <Text
                  size="article"
                  color={theme.components.xperimenta.card.subTitle}
                  marginTop="8px"
                >
                  {item.value}
                </Text>
                <InjectHtml
                  content={
                    item.label?.length
                      ? item.label
                      : lang === "pt"
                      ? "Sem informação"
                      : "No information"
                  }
                />
              </InjectHtmlRow>
            );
          })}
        </div>
      )}
      <div className="row no-gutters">
        {session?.startDate && (
          <div className="col col-sm-4 col-md-4 col-lg-4 col-xl-4">
            <Text
              size="small"
              color={theme.components.xperimenta.sessionDateAndTimeColor}
              lineHeight="1.3"
            >
              {lang === "pt"
                ? "Esclarece as tuas dúvidas"
                : "Get the answers to your questions"}
            </Text>
            <Text
              size="small"
              color={theme.components.xperimenta.sessionDateAndTimeColor}
              fontWeight="400"
              lineHeight="1.3"
            >
              {moment(session.startDate)
                .format("DD MMM, ")
                .toLowerCase()}
              {moment(session.startDate).format("HH:mm") +
                (session?.endDate
                  ? `-${moment(session.endDate).format("HH:mm")}`
                  : "")}
            </Text>
          </div>
        )}
        {session?.registrationLink && (
          <div className="col">
            <a
              href={session?.registrationLink}
              target="_blank"
              rel="noopener noreferrer"
            >
              <Button variant="default" title="inscrição" {...buttonProps}>
                {lang === "pt" ? "inscrição" : "registration"}
              </Button>
            </a>
          </div>
        )}
      </div>
    </BorderedCard>
  );
});

const Accordion = ({ section, item, sessions, theme, lang }) => {
  const [isOpen, setOpen] = useState(false);
  return (
    <GeneralAccordion onChange={() => setOpen(!isOpen)}>
      <GeneralAccordionItem>
        <GeneralAccordionItemTitle>
          <GeneralAccordionHeader>{section.title}</GeneralAccordionHeader>
          <GeneralAccordionTitle
            isOpen={isOpen}
            theme={theme}
            url={`/${lang}/${lang === "pt" ? "curso" : "course"}/${item.id}`}
          >
            {item.name}
          </GeneralAccordionTitle>
        </GeneralAccordionItemTitle>
        <GeneralAccordionItemBody>
          <Card
            key={item.id}
            theme={theme}
            data={item.list}
            session={
              sessions.length > 0 &&
              sessions.find(session => item.id === session?.id)
            }
            highlightColor={theme.components.xperimenta.sessionColor}
            lang={lang}
          />
        </GeneralAccordionItemBody>
      </GeneralAccordionItem>
    </GeneralAccordion>
  );
};

const Xperimenta = ({
  className,
  isLoading,
  hasError,
  data,
  sessions,
  theme,
  buttons,
  defaultValues,
  filters,
  onChangeFilter,
  lang,
  title
}) => {
  const hasList = data.find(degree => degree.list.length > 0);

  return (
    <Section className={className} hasHighContrastBorderTop={false}>
      {isLoading ? (
        <Loading theme={theme} />
      ) : hasError ? (
        <Error />
      ) : (
        <Fragment>
          <div className="container">
            <div className="row no-gutters ">
              <div className="offset-xl-3 offset-lg-4 offset-md-0 col-xl-7 col-lg-8 col-md-12 col ">
                <div className="row no-gutters justify-content-between">
                  <div className="col-xl-10 col-lg-10 col-md-10 col-sm-10">
                    <Text
                      size="xLarge"
                      fontWeight="500"
                      mobileXsMarginBottom="40px"
                      marginBottom="40px"
                    >
                      {title}
                    </Text>
                  </div>
                  <div className="col-xl-6 col-lg-6 col-md-6 col-sm-6">
                    <ContentContainer
                      paddingRight="12px"
                      mobileXsPaddingRight="0px"
                      mobileSmPaddingRight="12px"
                      mobileMdPaddingRight="12px"
                    >
                      {buttons.map((btn, index) => {
                        return (
                          <div key={index}>
                            <Select
                              variant="black"
                              options={btn.options}
                              placeholder={btn.title}
                              label={btn.title}
                              closeMenuOnSelect={false}
                              isMulti
                              valueContainer
                              hasMultiCustomStyles
                              isSearchable={false}
                              defaultValue={defaultValues.degree}
                              onChange={(action, option) =>
                                onChangeFilter(
                                  option.option || option.removedValue,
                                  btn.jsonField
                                )
                              }
                            />
                          </div>
                        );
                      })}
                    </ContentContainer>
                  </div>
                  <div className="col-xl-6 col-lg-6 col-md-6 col-sm-6">
                    <ContentContainer
                      paddingLeft="12px"
                      mobileXsPaddingLeft="0px"
                      mobileSmPaddingLeft="12px"
                      mobileMdPaddingLeft="12px"
                    >
                      {filters.map((res, index) => {
                        return (
                          <div
                            key={index}
                            className="row no-gutters mt-xl-0 mt-lg-0 mt-md-0 mt-sm-0 mt-3"
                          >
                            <div className="col">
                              <Select
                                variant="black"
                                defaultValue={defaultValues.knowledgeAreas}
                                options={res.options}
                                placeholder={res.title}
                                label={res.title}
                                isMulti
                                valueContainer
                                hasMultiCustomStyles
                                isClearable
                                closeMenuOnSelect={false}
                                onChange={(action, option) =>
                                  onChangeFilter(
                                    option.option || option.removedValue,
                                    res.jsonField
                                  )
                                }
                              />
                            </div>
                          </div>
                        );
                      })}
                    </ContentContainer>
                  </div>
                </div>
              </div>
            </div>
          </div>
          <div className="container">
            <div className="row no-gutters">
              <div className="col mt-5">
                {!hasList ? (
                  <Error
                    text={lang === "pt" ? "Sem Conteúdo" : "No Content"}
                    className="offset-xl-3 offset-lg-4 offset-md-0 col-xl-7 col-lg-8 col-md-12 col "
                    minHeight={"100px"}
                  />
                ) : (
                  data
                    .filter(opt => opt?.list?.length > 0)
                    .map(section => {
                      return (
                        <div key={section.title}>
                          {section.list.map(item => {
                            return (
                              <div className="row no-gutters" key={item.id}>
                                <div className="offset-xl-3 offset-lg-4 offset-md-0 col-xl-7 col-lg-8 col-md-12 col">
                                  <Accordion
                                    section={section}
                                    item={item}
                                    sessions={sessions}
                                    theme={theme}
                                    lang={lang}
                                    title={title}
                                  />
                                </div>
                              </div>
                            );
                          })}
                        </div>
                      );
                    })
                )}
              </div>
            </div>
          </div>
        </Fragment>
      )}
    </Section>
  );
};

Xperimenta.defaultProps = {
  title: "Cursos",
  className: "",
  isLoading: false,
  hasError: false,
  defaultValues: {
    degree: "",
    knowledgeAreas: ""
  },
  buttons: [],
  filters: [],
  data: [],
  sessions: []
};

const sessionType = PropTypes.shape({
