export default withTheme(OneFact);
import React, { useRef } from "react";
import PropTypes from "prop-types";
//Styles
import styled, { withTheme } from "styled-components";
//Components
import Slider from "react-slick";
import { Section } from "../../atoms/index";
import { CustomArrow, SectionHeader, StudentCard } from "../../molecules/index";

const About = ({
  className,
  title,
  textColor,
  data,
  showHeader,
  titleProps,
  backgroundColor,
  theme
}) => {
  const sliderRef = useRef(null);

  const onHandleTabNavigation = e => {
    if (e.key === "Tab" && e.shiftKey) {
      sliderRef.current.slickPrev();
    }

    if (e.key === "Tab") {
      sliderRef.current.slickNext();
    }
  };

  return (
    <Section
      className={className}
      backgroundColor={
        theme.highContrast.backgroundColor ||
        backgroundColor ||
        theme.components.about.backgroundColor
      }
    >
      {showHeader ? (
        <SectionHeader
          textColor={theme.highContrast.text || textColor}
          {...titleProps}
        >
          {title}
        </SectionHeader>
      ) : null}
      <div className="container about-container">
        <Slider
          ref={sliderRef}
          tabIndex={-1}
          useTransform={false}
          slidesToShow={3}
          slidesToScroll={1}
          speed={500}
          infinite={false}
          centerMode={false}
          centerPadding={0}
          dots={false}
          initialSlide={0}
          customPaging={i => {
            return <button aria-label={`Aluno ${i + 1}`}>&nbsp;</button>;
          }}
          prevArrow={
            <CustomArrow
              ariaLabel="slide anterior"
              color={theme.components.about.arrowColor}
              left
              darken
            />
          }
          nextArrow={
            <CustomArrow
              ariaLabel="próximo slide"
              color={theme.components.about.arrowColor}
              darken
            />
          }
          responsive={[
            {
              breakpoint: 1199,
              settings: {
                slidesToShow: 3
              }
            },
            {
              breakpoint: 991,
              settings: {
                slidesToShow: 2
              }
            },
            {
              breakpoint: 767,
              settings: {
                slidesToShow: 1
              }
            },
            {
              breakpoint: 575,
              settings: {
                slidesToShow: 1,
                arrows: false,
                dots: true
              }
            }
          ]}
        >
          {data.length > 0 &&
            data.map((item, index) => {
              return (
                <StudentCard
                  key={index}
                  text={item.text}
                  src={item.person.image}
                  alt={item.person.alt}
                  name={item.person.name}
                  about={item.person.about}
                  imageBgColor={item.backgroundColor}
                  onKeyDown={onHandleTabNavigation}
                />
              );
            })}
        </Slider>
      </div>
    </Section>
  );
};

const defaults = {
  title: "Title",
  showHeader: true,
  data: []
};

const types = {
  className: PropTypes.string,
  title: PropTypes.string,
  textColor: PropTypes.string,
  showHeader: PropTypes.bool,
  theme: PropTypes.object,
  titleProps: PropTypes.object,
  data: PropTypes.oneOfType([PropTypes.array, PropTypes.object]),
  backgroundColor: PropTypes.string
};

About.propTypes = types;
About.defaultProps = defaults;

const AboutWithStyles = styled(About)`
  .about-container {
    padding-right: 0;
    padding-left: 0;
  }

  .slick-prev {
    left: -25px;
  }
  .slick-next {
    right: -25px;
  }
  .slick-next,
  .slick-prev {
    width: auto;
    height: 36px;
    top: 96px;
  }

  .slick-next:before,
  .slick-prev:before {
    content: none;
  }

  .slick-dots {
    width: 100%;
    bottom: 0;
    position: relative;
    margin-top: 10px;
    text-align: right;
    padding: 0 15px;
  }

  .slick-dots li {
    margin: 0 5px;
    &:last-child {
      margin-right: 0;
    }
