import React from "react";
import PropTypes from "prop-types";
//Styles
import styled, { withTheme } from "styled-components";
//Components
import {
  ScreenReaderText,
  Text,
  Title,
  ContentContainer,
  VerticalList,
  ListItem,
  Section
} from "../../atoms";
import { PlusButton, SectionHeader, MarkdownRenderer } from "../../molecules";
import ImageWrapper from "../../organisms/ImageWrapper";
//Utils
import { withSize } from "react-sizeme";
import { compose } from "../../../utils/utilities";
//Context
import { useConfig } from "../../../context/ConfigContext";

const Campus = ({
  className,
  textColor,
  title,
  resource,
  showHeader,
  size,
  titleProps,
  showPlusButton,
  isMarkdown,
  contentMaxHeight,
  height,
  data,
  theme,
  activeColor
}) => {
  const { link: Link, formattedMessage: FormattedMessage } = useConfig();
  // doing this logic within a string literal
  // for example: `${position === 'right' ? '-30px' : null}`
  // would result in right="null" (null becoming a string)
  // now null isnt converted to a string and only the necessary prop is added
  const computedPlusButtonPosition = position => ({
    right: position === "right" ? "-30px" : null,
    left: position === "right" ? null : "-30px"
  });

  return (
    <Section
      className={className}
      paddingTop={`${showHeader ? "70px" : "0"}`}
      paddingBottom={`${showHeader ? "0" : "0"}`}
      mdPaddingTop="0"
      backgroundColor={theme.highContrast.backgroundColor}
    >
      <ScreenReaderText>
        <Title text="Áreas" />
      </ScreenReaderText>
      {showHeader ? (
        <SectionHeader
          textColor={theme.highContrast.text || textColor}
          marginBottom="50px"
          {...titleProps}
        >
          {resource ? (
            <FormattedMessage id={resource} defaultMessage={title} />
          ) : (
            title
          )}
        </SectionHeader>
      ) : null}

      {data.map(({ backgroundPosition = "center 40%", ...item }, index) => {
        let changeBg =
          index % 2 === 0
            ? theme.highContrast.lightBackgroundColor
            : theme.highContrast.backgroundColor;
        let backgroundColor = changeBg || item.backgroundColor;
        return (
          <ContentContainer
            key={index}
            backgroundColor={backgroundColor}
            height={item.height || height}
            overflow="hidden"
          >
            <div className="row no-gutters align-items-center h-100">
              <div
                className={`col-xl-${item.col[0]} col-lg-${
                  item.col[0]
                } col-md-${item.col[0]} col-sm-6 col-6 ${
                  item.position === "right" ? "order-1" : null
                }`}
              >
                <ContentContainer
                  marginLeft={`${
                    item.position === "right"
                      ? "70px"
                      : `${(size.width - 1140) / 2 + 15}px`
                  }`}
                  mobileLgMarginLeft={`${
                    item.position === "right"
                      ? "50px"
                      : `${(size.width - 960) / 2 + 15}px`
                  }`}
                  mobileMdMarginLeft={`${
                    item.position === "right"
                      ? "50px"
                      : `${(size.width - 720) / 2 + 15}px`
                  }`}
                  mobileSmMarginLeft={`${
                    item.position === "right"
                      ? "30px"
                      : `${(size.width - 540) / 2 + 15}px`
                  }`}
                  mobileXsMarginLeft={`${
                    item.position === "right" ? "30px" : "15px"
                  }`}
                  overflow="hidden"
                  marginRight={`${
                    item.position !== "right"
                      ? "70px"
                      : `${(size.width - 1140) / 2 + 15}px`
                  }`}
                  mobileLgMarginRight={`${
                    item.position !== "right"
                      ? "50px"
                      : `${(size.width - 960) / 2 + 15}px`
                  }`}
                  mobileMdMarginRight={`${
                    item.position !== "right"
                      ? "50px"
                      : `${(size.width - 720) / 2 + 15}px`
                  }`}
                  mobileSmMarginRight={`${
                    item.position !== "right"
                      ? "30px"
                      : `${(size.width - 540) / 2 + 15}px`
                  }`}
                  mobileXsMarginRight={`${
                    item.position !== "right" ? "30px" : "15px"
                  }`}
                  position="relative"
                  zIndex="2"
                >
                  <SectionHeader
                    aria-label={item.text}
                    tag="h3"
                    fontSize="28px"
                    textColor={theme.highContrast.link || item.textColor}
                    href={item.href}
                    mobileSmFontSize="26px"
                    mobileXsFontSize="20px"
                    hasContainer={false}
                    marginBottom="0px"
                    outlineOffset="-2px"
                    externalLink={item.externalLink}
                  >
                    {item.resource ? (
                      <FormattedMessage
                        id={item.resource}
                        defaultMessage={item.text}
                      />
                    ) : (
                      item.text
                    )}
                  </SectionHeader>

                  <div className="campus-items">
                    <ContentContainer
                      marginTop="15px"
                      maxHeight={contentMaxHeight}
                      overflow="hidden"
                    >
                      {item.hasText ? (
                        isMarkdown ? (
                          <MarkdownRenderer
                            content={item.description}
                            activeColor={activeColor}
                          />
                        ) : (
                          <Text
                            lineHeight="1.6"
                            size="article"
                            fontWeight="300"
                            text={theme.highContrast.text || item.description}
                          />
                        )
                      ) : (
                        <VerticalList>
                          {item.list.map((item, index) => {
                            return (
                              <ListItem marginBottom="5px" key={index}>
                                <Link
                                  cursor="pointer"
                                  fontSize="18px"
                                  color={theme.highContrast.link || item.color}
                                  aria-label={item.text}
                                  href={item.href}
                                  outlineOffset="-2px"
                                >
                                  {item.resource ? (
                                    <FormattedMessage
                                      id={item.resource}
                                      defaultMessage={item.text}
                                    />
                                  ) : (
                                    item.text
                                  )}
                                </Link>
                              </ListItem>
                            );
                          })}
                        </VerticalList>
                      )}
                    </ContentContainer>
                  </div>
                </ContentContainer>
              </div>
              <div
                className={`col-xl-${item.col[1]} col-lg-${
                  item.col[1]
                } col-md-${item.col[1]} col-sm-6 col-6 ${
                  item.position === "right" ? "order-0" : null
                }`}
              >
                <ContentContainer height={item.height || height}>
                  {showPlusButton && (
                    <PlusButton
                      backgroundColor={backgroundColor}
                      size="60px"
                      height="100%"
                      zIndex="1"
                      {...computedPlusButtonPosition(item.position)}
                      as="a"
                      href={item.href}
                    >
                      <Text
                        fontSize="50px"
                        fontWeight="100"
                        color={theme.highContrast.link || item.textColor}
                        text={theme.icons.plus}
                        lineHeight="1"
                      />
                    </PlusButton>
                  )}
                  <ImageWrapper
                    height="350px"
                    size="md"
                    backgroundColor={backgroundColor}
                    data={{ url: item.backgroundImage }}
                    aria-label={item.alt}
                    alt={item.alt}
                    imagePosition={backgroundPosition}
                    hasEndpoint={false}
                    endpoint={() => null}
                    isVector={
                      item.isVector !== undefined ? item.isVector : false
                    }
                    isLocal={item.isLocal}
                  />
                </ContentContainer>
              </div>
            </div>
          </ContentContainer>
        );
      })}
    </Section>
  );
};

const types = {
  className: PropTypes.string,
  textColor: PropTypes.string,
  title: PropTypes.string,
  data: PropTypes.oneOfType([
    PropTypes.arrayOf(
      PropTypes.shape({
        backgroundPosition: null
      })
    ),
    PropTypes.object
  ]),
  showHeader: PropTypes.bool,
  size: PropTypes.object,
  titleProps: PropTypes.object,
  showPlusButton: PropTypes.bool,
  isMarkdown: PropTypes.bool,
  contentMaxHeight: PropTypes.string,
  height: PropTypes.string,
  resource: PropTypes.string,
  theme: PropTypes.object,
  activeColor: PropTypes.string
};

const defaults = {
  showPlusButton: true,
  isMarkdown: false,
  contentMaxHeight: "190px",
  height: "350px",
  activeColor: "#0EB4BD"
};

Campus.propTypes = types;
