const Campos = ({
  className,
  showHeader,
  title,
  theme,
  titleProps,
  textColor,
  data,
  resource,
  isMarkdown,
  supportsCssGrid,
  activeColor,
  showPlusButton,
  backgroundColor
}) => {
  const { formattedMessage: FormattedMessage } = useConfig();
  const { textColor: themeTextColor } = theme.components.campos;
  return (
    <Section
      className={className}
      paddingTop={`${showHeader ? "70px" : "0"}`}
      paddingBottom={`${showHeader ? "0" : "0"}`}
      mdPaddingTop="0"
    >
      <ScreenReaderText>
        <Text as="h2" text="Áreas" />
      </ScreenReaderText>
      {showHeader ? (
        <SectionHeader
          textColor={theme.highContrast.text || textColor || themeTextColor}
          marginBottom="50px"
          {...titleProps}
        >
          {resource ? (
            <FormattedMessage id={resource} defaultMessage={title} />
          ) : (
            title
          )}
        </SectionHeader>
      ) : null}
      {data.map((item, index) => {
        let isOdd = index % 2;
        return supportsCssGrid ? (
          <ColumnsCard
            key={index}
            src={item.backgroundImage}
            title={item.text}
            links={item.list}
            inverted={isMarkdown ? isOdd : item.inverted}
            imageGridColumn={
              isMarkdown ? (isOdd ? "1 / 7" : "7 / -1") : item.inverted
            }
            textGridColumn={
              isMarkdown ? (isOdd ? "7 / 12" : "2 / 7") : item.inverted
            }
            isMarkdown={isMarkdown}
            activeColor={activeColor}
            showPlusButton={showPlusButton}
            backgroundColor={backgroundColor}
            {...item}
          />
        ) : (
          /* Fallback Card*/
          <LayeredCard
            key={index}
            src={item.backgroundImage}
            title={item.text}
            links={item.list}
            isMarkdown={isMarkdown}
            inverted={isMarkdown ? isOdd : item.inverted}
            activeColor={activeColor}
            showPlusButton={showPlusButton}
            backgroundColor={backgroundColor}
            {...item}
          />
        );
      })}
    </Section>
  );
};

