export default withTheme(PlaceholderWithStyles);
import React, { useState } from "react";
//Styles
import styled, { withTheme } from "styled-components";
import PropTypes from "prop-types";
//Components
import {
  Text,
  Button,
  FadeIn,
  Section,
  ContentContainer
} from "../../atoms/index";
import Placeholder from "./placeholder";
//Context
import { useConfig, ConfigContext } from "../../../context/ConfigContext";

function Square({ isDisable, onClick, value }) {
  return (
    <button
      className={`square ${isDisable ? "" : "square-active"}`}
      onClick={onClick}
    >
      {value}
    </button>
  );
}

Square.propTypes = {
  isDisable: PropTypes.bool,
  onClick: PropTypes.func,
  value: PropTypes.string
};

function Board({ squares, onClick, isDisable }) {
  const renderSquare = i => {
    return (
      <Square
        value={squares[i]}
        onClick={() => onClick(i)}
        isDisable={isDisable}
      />
    );
  };

  return (
    <div>
      <div className="board-row">
        {renderSquare(0)}
        {renderSquare(1)}
        {renderSquare(2)}
      </div>
      <div className="board-row">
        {renderSquare(3)}
        {renderSquare(4)}
        {renderSquare(5)}
      </div>
      <div className="board-row">
        {renderSquare(6)}
        {renderSquare(7)}
        {renderSquare(8)}
      </div>
    </div>
  );
}

Board.propTypes = {
  squares: PropTypes.array,
  onClick: PropTypes.func,
  isDisable: PropTypes.bool
};

class Game extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      history: [
        {
          squares: Array(9).fill(null)
        }
      ],
      stepNumber: 0,
      xIsNext: true,
      status: "",
      resource: "app.journal.errorHandler.next.player",
      isDisable: false
    };
  }

  componentDidMount() {
    this.onStatus();
  }

  handleClick(i) {
    const history = this.state.history.slice(0, this.state.stepNumber + 1);
    const current = history[history.length - 1];
    const squares = current.squares.slice();
    if (calculateWinner(squares) || squares[i]) {
      return;
    }
    squares[i] = this.state.xIsNext ? "4" : "0";
    this.setState({
      history: history.concat([
        {
          squares: squares
        }
      ]),
      stepNumber: history.length,
      xIsNext: !this.state.xIsNext
    });
    setTimeout(() => {
      this.onStatus();
    }, 50);
  }

  jumpTo(step) {
    this.setState({
      stepNumber: step,
      xIsNext: step % 2 === 0,
      status: step === 0 ? "4" : this.state.status,
      resource: "app.journal.errorHandler.next.player",
      isDisable: step !== 0
    });
  }

  onStatus() {
    const history = this.state.history;
    const current = history[this.state.stepNumber];
    const winner = calculateWinner(current.squares);

    if (winner) {
      this.setState({
        status: winner,
        resource: "app.journal.errorHandler.winner",
        isDisable: true
      });
    } else {
      if (history.length > 9) {
        this.setState({
          status: "",
          resource: "app.journal.errorHandler.tie",
          isDisable: true
        });
        return;
      }
      this.setState({
        status: this.state.xIsNext ? "4" : "0",
        resource: "app.journal.errorHandler.next.player",
        isDisable: false
      });
    }
  }

  render() {
    const history = this.state.history;
    const current = history[this.state.stepNumber];
    let { formattedMessage: FormattedMessage } = this.context;

    return (
      <div className="game">
        <div className="game-board">
          <Board
            squares={current.squares}
            onClick={i => this.handleClick(i)}
            isDisable={this.state.isDisable}
          />
        </div>
        {this.state.isDisable === true && (
          <FadeIn animationTime={0.5} className="game-info-container">
            <Text size="large" fontWeight="400" marginBottom="30px">
              <FormattedMessage
                id={this.state.resource}
                defaultMessage={this.state.status}
                values={{ player: this.state.status }}
              />
            </Text>
            <Button variant="default" onClick={() => this.jumpTo(0)}>
              <FormattedMessage
                id="app.journal.errorHandler.button.restart"
                defaultMessage="Jogar de novo"
              />
            </Button>
          </FadeIn>
        )}
      </div>
    );
  }
}

Game.contextType = ConfigContext;

function calculateWinner(squares) {
  const lines = [
    [0, 1, 2],
    [3, 4, 5],
    [6, 7, 8],
    [0, 3, 6],
    [1, 4, 7],
    [2, 5, 8],
    [0, 4, 8],
    [2, 4, 6]
  ];
  for (let i = 0; i < lines.length; i++) {
    const [a, b, c] = lines[i];
    if (squares[a] && squares[a] === squares[b] && squares[a] === squares[c]) {
      return squares[a];
    }
  }
  return null;
}

const ErrorHandler = ({
  className,
  theme,
  description,
  errorCode,
  searchHref
}) => {
  const [isPlaying, setPlaying] = useState(false);
  const { link: Link, formattedMessage: FormattedMessage } = useConfig();
  const onPlay = () => {
    setPlaying(true);
  };

  const message = errorCode => {
    switch (errorCode) {
      case 404:
        return {
          title: "app.journal.errorHandler.message.title",
          firstPart: "app.journal.errorHandler.message.firstPart",
          secondPart: "app.journal.errorHandler.message.secondPart",
          homepageLink: "app.journal.errorHandler.homepage.link",
          searchLink: "app.journal.errorHandler.search.link"
        };
      default:
        return {
          title: "app.journal.errorHandler.general.message.title",
          firstPart: "app.journal.errorHandler.general.message.firstPart",
          secondPart: "app.journal.errorHandler.general.message.secondPart",
          homepageLink: "app.journal.errorHandler.general.homepage.link",
          searchLink: "app.journal.errorHandler.general.search.link"
        };
    }
  };

  return (
    <Section
      backgroundColor={theme.components.errorHandler.backgroundColor}
      hasHighContrastBorderTop={false}
    >
      <div className={className}>
        <div className="container">
          <div className="row align-items-center justify-content-md-start justify-content-center">
            <div className="offset-xl-3 offset-0 col-md col-12">
              <Text
                as="h3"
                color={theme.components.errorHandler.color}
                size="large"
                fontWeight="400"
              >
                <FormattedMessage
                  id={message(errorCode).title}
                  defaultMessage={"Ups! Página não encontrada"}
                />
              </Text>
              <ContentContainer
                marginTop="30px"
                marginRight="60px"
                mobileMdMarginRight="30px"
                mobileSmMarginRight="0"
                smMarginbottom="50px"
              >
                <Text
                  color={theme.components.errorHandler.color}
                  fontSize="14px"
                  fontWeight="300"
                  lineHeight="1.6"
                >
                  <FormattedMessage
                    id={message(errorCode).firstPart}
                    defaultMessage="A página à qual tentou aceder não se encontra disponível . Esta pode ter sido removida, estar temporariamente indisponível ou o seu endereço estar incorreto. Faça uma "
                  />{" "}
                  <Link
                    href={searchHref}
                    color={theme.components.errorHandler.linkColor}
                    fontSize="14px"
                    textDecoration="underline"
                    fontWeight="300"
                    cursor="pointer"
                  >
                    <FormattedMessage
                      id={message(errorCode).searchLink}
                      defaultMessage="pesquisa"
                    />
                  </Link>{" "}
                  <FormattedMessage
                    id={message(errorCode).secondPart}
                    defaultMessage="ou volte à"
                  />{" "}
                  <Link
                    href="/"
                    color={theme.components.errorHandler.linkColor}
                    fontSize="14px"
                    textDecoration="underline"
                    fontWeight="300"
                    cursor="pointer"
                  >
                    <FormattedMessage
                      id={message(errorCode).homepageLink}
                      defaultMessage="página inicial"
                    />
                  </Link>
                  .
                </Text>
              </ContentContainer>
              {description && description.url && (
                <ContentContainer
                  marginTop="30px"
                  marginRight="60px"
                  mobileMdMarginRight="30px"
                  mobileSmMarginRight="0"
                  smMarginbottom="50px"
                >
                  <Link
                    href={description.location}
                    color={theme.components.errorHandler.linkColor}
                    fontSize="14px"
                    textDecoration="underline"
                    fontWeight="300"
                    cursor="pointer"
                  >
                    {description.url}
                  </Link>
                </ContentContainer>
              )}
            </div>

            <div className="col-auto">
              {/*Game only for 404 page*/}
              {errorCode === 404 ? (
                isPlaying ? (
                  <Game />
                ) : (
                  <Placeholder onHandlePlay={onPlay} />
                )
              ) : null}
            </div>
          </div>
        </div>
      </div>
    </Section>
  );
};

const types = {
