const Files = ({
  className,
  theme,
  title,
  downloadAll,
  activeColor,
  data,
  onDownload,
  onDownloadAll,
  size,
  isLoading,
  lang
}) => {
  const textColor = theme.components.files.textColor;
  const lightTextColor = theme.components.files.lightText;
  const localeData = useMemo(() => {
    return {
      pt: {
        name: "nome",
        type: "tipo",
        date: "data",
        description: "descrição",
        size: "tamanho",
        downloadAll: "Descarregar todos"
      },
      en: {
        name: "name",
        type: "type",
        date: "date",
        description: "description",
        size: "size",
        downloadAll: "Download all"
      }
    };
  }, []);
  return (
    <Section className={className}>
      <div className="container">
        <HeaderContainer>
          <Text as="h2" size="xLarge" fontWeight="500" color={textColor}>
            {title}
          </Text>
          {size.width >= 767.98 && (
            <Link
              as="button"
              border="none"
              background="transparent"
              size="mediumSmall"
              textDecoration="underline"
              lighten
              onClick={() => onDownloadAll(data)}
              color={theme.components.files.buttonColor}
            >
              {downloadAll}
            </Link>
          )}
        </HeaderContainer>
        {isLoading ? (
          <ContentLoading />
        ) : (
          <Fragment>
            {size.width >= 767.98 ? (
              <TableStyles activeColor={activeColor}>
                <table>
                  <thead>
                    <tr>
                      <th>{localeData[lang].name}</th>
                      <th>{localeData[lang].type}</th>
                      <th>{localeData[lang].description}</th>
                      <th>{localeData[lang].date}</th>
                      <th>{localeData[lang].size}</th>
                      <th>&nbsp;</th>
                    </tr>
                  </thead>
                  <tbody>
                    {data.map(item => {
                      return (
                        <tr key={item.id}>
                          <td>{item.name}</td>
                          <td>{item.type.toUpperCase()}</td>
                          <td>{item.description}</td>
                          <td>{moment(item.date).format("DD/MM/YYYY")}</td>
                          <td>{item.size}</td>
                          <td>
                            <IconButton onClick={() => onDownload(item)}>
                              <Icon
                                name="fal fa-arrow-to-bottom"
                                size="small"
                                color={textColor}
                              />
                            </IconButton>
                          </td>
                        </tr>
                      );
                    })}
                  </tbody>
                </table>
              </TableStyles>
            ) : (
              <ContentContainer>
                {data.map(item => {
                  return (
                    <BorderedCard key={item.id} cardIsLink={false}>
                      <Text marginBottom="5px" fontWeight="400">
                        {item.name}
                      </Text>
                      <Text marginBottom="20px">{item.description}</Text>
                      <div className="d-flex align-items-center">
                        <Text color={lightTextColor}>{item.size}</Text>
                        <Text
                          color={lightTextColor}
                          marginLeft="20px"
                          marginRight="20px"
                        >
                          {item.type.toUpperCase()}
                        </Text>
                        <IconButton
                          marginLeft="auto"
                          onClick={() => onDownload(item)}
                        >
                          <Icon
                            name="fal fa-arrow-to-bottom"
                            size="small"
                            color={textColor}
                          />
                        </IconButton>
                      </div>
                    </BorderedCard>
                  );
                })}
              </ContentContainer>
            )}
            {size.width <= 767.98 && (
              <ContentContainer
                marginTop="50px"
                className="d-flex justify-content-center"
              >
                <Button onClick={() => onDownloadAll(data)} variant="default">
                  {localeData[lang].downloadAll}
                </Button>
              </ContentContainer>
            )}
          </Fragment>
        )}
      </div>
    </Section>
  );
};

