  activeTab,
  onChangeTab,
  hasLink,
  activeColor,
  isMobile
}) => {
  return (
    <Section
      className={className}
      backgroundColor={theme.components.subWebWarnings.backgroundColor}
    >
      <SectionHeader
        href="/"
        hasContainer={true}
        hasLine={hasLink}
        hasLink={hasLink}
        textColor={theme.components.subWebWarnings.titleColor}
        marginBottom={tabs.length <= 0 && "0"}
      >
        {title}
      </SectionHeader>
      {isLoading ? (
        <Loading />
      ) : (
        <Fragment>
          {/*Tabs*/}
          {tabs.length > 1 && (
            <ScrollableTabs marginBottom="0">
              {tabs.map((tab, i) => {
                return (
                  <ScrollableTab
                    key={i}
                    isActive={tab.id === activeTab}
                    text={tab.text}
                    theme={theme}
                    activeColor={activeColor}
