                                as="span"
                                size="large"
                                fontWeight="200"
                                marginLeft="10px"
                                color={
                                  theme.components.subWebDepartmentCourses
                                    .subTitleColor
                                }
                              >
                                {section.subtitle}
                              </Text>
                            )}
                          </Text>

                          {section.list.map(item => {
                            return (
                              <Card
                                url={`/${lang}/${
                                  lang === "pt" ? "curso" : "course"
                                }/${item.id}`}
                                key={item.id}
                                title={item.name}
                                theme={theme}
                                data={item.list}
                              />
                            );
                          })}
                        </div>
                      );
                    })
                )}
              </div>
            </div>
          </div>
        </Fragment>
      )}
    </Section>
  );
};

