import React, { Fragment } from "react";
import PropTypes from "prop-types";
//Styles
import styled, { withTheme } from "styled-components";
//components
import { Text, Section, ContentContainer } from "./../../../atoms";
import {
  ScrollableTabs,
  ScrollableTab,
  PeopleCard,
  Error,
  ScrollableTabsLoading
} from "./../../../molecules";
import { withSize } from "react-sizeme";
//Locales
import { compose } from "../../../../utils/utilities";
import Loading from "./Loading";
//Context
import { useConfig } from "../../../../context/ConfigContext";

const People = ({
  title,
  className,
  isLoading,
  selected,
  tabs,
  data,
  hasError,
  theme,
  onChangeTab,
  isTabsLoading,
  lang,
  popcorn,
  activeColor
}) => {
  const { portalAPI } = useConfig();

  return (
    <Section
      className={className}
      hasBottomSpacing={false}
      hasHighContrastBorderTop={false}
    >
      <div className="container">
        <div className="row no-gutters">
          <div className="offset-lg-3 col">
            <Text as="h2" size="xLarge" fontWeight="500" marginBottom="70px">
              {title}
            </Text>
          </div>
        </div>
      </div>

      {isTabsLoading ? (
        <ContentContainer marginBottom="50px">
          <ScrollableTabsLoading offsetClasses="offset-lg-3" />
        </ContentContainer>
      ) : (
        <ScrollableTabs offSetClasses="offset-lg-3">
          {tabs.map((item, i) => {
            return (
              <ScrollableTab
                key={i}
                text={item.label}
                isActive={item.value === selected}
                onClick={() => onChangeTab(item.value)}
                activeColor={activeColor}
                {...theme}
              />
            );
          })}
        </ScrollableTabs>
      )}

      {isLoading ? (
        <Loading />
      ) : hasError ? (
        <Error />
      ) : (
        <Fragment>
          <div className="container">
            {data.map((res, i) => {
              return (
                <div key={i} className="row no-gutters people-row">
                  <div className="col-lg-3 col-12">
                    <ContentContainer
                      width="60%"
                      mobileMdWidth="100%"
                      mdMarginBottom="30px"
                    >
                      <Text
                        size="large"
                        color={
                          theme.highContrast.text ||
                          activeColor ||
                          theme.components.subWebPeople.titleColor
                        }
                        fontWeight="400"
                      >
                        {res.title}
                      </Text>
                    </ContentContainer>
                  </div>
                  <div className="col">
                    <div className="row">
                      {res.list.map(item => {
                        return (
                          <div
                            key={item.uuid !== undefined ? item.uuid : item.id}
                            className="col-lg-3 col-md-3 col-sm-4 col-6"
                          >
                            <PeopleCard
                              src={
                                popcorn
                                  ? `${portalAPI}/person/${item.id}/photo?getdefault=true`
                                  : item.image
                              }
                              name={item.name}
                              email={item.email[0]}
                              extension={item.contactsList}
                              href={`/${lang}/p/${item.id}`}
                              hasPhoto={item.showPhoto}
                              lang={lang}
                            />
                          </div>
                        );
                      })}
                    </div>
                  </div>
                </div>
              );
            })}
          </div>
        </Fragment>
      )}
    </Section>
  );
};

