import React, { Fragment } from "react";
import PropTypes from "prop-types";
//Styles
import styled, { withTheme } from "styled-components";
//components
import { Section, Text, Button, ContentContainer } from "./../../../atoms";
import {
  ContentLoading,
  SectionHeader,
  MarkdownRenderer
} from "./../../../molecules";

const TextContainer = styled.div`
  @media (min-width: 768px) {
    column-count: 2;
    -moz-column-count: 2;
    -webkit-column-count: 2;
    column-gap: 30px;
    -moz-column-gap: 30px;
    -webkit-column-gap: 30px;
  }
`;

const Schedule = ({
  className,
  title,
  subTitle,
  description,
  action,
  isLoading,
  isClosed,
  hasLink,
  href,
  withColumns,
  theme,
  data,
  activeColor
}) => {
  const backgroundColor = isClosed
    ? theme.components.subWebSchedule.backgroundColor.isClosed
    : theme.components.subWebSchedule.backgroundColor.isOpen;
  const textColor = isClosed
    ? theme.components.subWebSchedule.textColor.isClosed
    : theme.components.subWebSchedule.textColor.isOpen;
  const linkColor = isClosed
    ? theme.components.subWebSchedule.linkColor.isClosed
    : theme.components.subWebSchedule.linkColor.isOpen;

  return (
    <Section className={className} backgroundColor={backgroundColor}>
      <SectionHeader
        href={href}
        hasLine={hasLink}
        hasLink={hasLink}
        hasContainer={true}
        textColor={hasLink ? linkColor : textColor}
      >
        {title}
      </SectionHeader>
      <div className="container">
        {isLoading ? (
          <ContentLoading maskBgColor={backgroundColor} />
        ) : (
          <Fragment>
            {withColumns ? (
              <div className="row">
                <div className="col-md col-12 d-flex flex-column justify-content-center">
                  <ContentContainer smMarginbottom="50px">
                    <Text size="large" fontWeight="400" color={textColor}>
                      {subTitle}
                    </Text>
                    <Text size="article" color={textColor}>
                      {description}
                    </Text>
                  </ContentContainer>
                </div>

                {data.length > 0 &&
                  data.map((item, index) => {
                    return (
                      <div key={index} className="col-md col-12">
                        <ContentContainer smMarginbottom="50px">
                          <Text
                            size="article"
                            fontWeight="400"
                            color={textColor}
                            marginBottom="20px"
                          >
                            {item.title}
                          </Text>
                          <Text size="large" fontWeight="400" color={textColor}>
                            <time>{item.subTitle}</time>
                          </Text>
                          <Text
                            size="article"
                            color={textColor}
                            marginBottom="20px"
                          >
                            {item.description}
                          </Text>
                          <MarkdownRenderer
                            activeColor={activeColor}
                            content={item.content}
                          />
                        </ContentContainer>
                      </div>
                    );
                  })}
              </div>
            ) : (
              <TextContainer>
                {data.length > 0 &&
                  data.map((item, index) => {
                    return (
                      <MarkdownRenderer
                        activeColor={activeColor}
                        key={index}
                        content={item.content}
                      />
                    );
                  })}
              </TextContainer>
            )}
            {action.show && (
              <ContentContainer
                marginTop={withColumns ? "15px" : "50px"}
                smMargintop="50px"
              >
                <Button
                  as="a"
                  href={action.href}
                  display="inline-flex"
                  textDecoration="none"
                  border={`1px solid ${textColor}`}
                  color={textColor}
                  hoverColor={backgroundColor}
                  hoverBackgroundColor={textColor}
                  backgroundColor={backgroundColor}
                >
                  {action.text}
                </Button>
              </ContentContainer>
            )}
          </Fragment>
        )}
      </div>
    </Section>
  );
};

const types = {
  className: PropTypes.string,
  isLoading: PropTypes.bool,
  theme: PropTypes.object,
  title: PropTypes.string,
  subTitle: PropTypes.string,
  description: PropTypes.string,
  isClosed: PropTypes.bool,
  href: PropTypes.string,
  hasLink: PropTypes.bool,
  withColumns: PropTypes.bool,
