  Button
} from "./../../../atoms";
import { EventCard, SectionHeader } from "./../../../molecules";
import Loading from "./Loading";

const CardsContainer = styled.div`
  margin-top: 50px;

  // Medium devices (tablets, less than 992px)
  @media (max-width: 991px) {
    .card-md-order-left {
      .triangle {
        right: initial;
        left: -48px;
        transform: rotate(-90deg);
      }
    }

    .card-md-order-right {
      .triangle {
        left: initial;
        right: -48px;
        transform: rotate(90deg);
      }
    }
  }
`;

const Events = ({
  title,
  description,
  href,
  hasLink,
  theme,
  hasImageSection,
  imageCover,
  alt,
  isLoading,
  buttonLink,
  hasButton,
  data
}) => {
  const images = [
    "https://images.pexels.com/photos/2265082/pexels-photo-2265082.jpeg?auto=compress&cs=tinysrgb&dpr=1&w=500",
    "https://images.pexels.com/photos/3608533/pexels-photo-3608533.jpeg?auto=compress&cs=tinysrgb&dpr=1&w=500",
    "https://images.pexels.com/photos/1953444/pexels-photo-1953444.jpeg?auto=compress&cs=tinysrgb&dpr=1&w=500",
    "https://images.pexels.com/photos/4108004/pexels-photo-4108004.jpeg?auto=compress&cs=tinysrgb&dpr=1&w=500",
    "https://images.pexels.com/photos/1713953/pexels-photo-1713953.jpeg?auto=compress&cs=tinysrgb&dpr=2&h=750&w=1260"
  ];

  const getRowTypeProps = type => {
    switch (type) {
      case "left":
        return [
          {
            className: "card-md-order-right",
            changeXlTriangleOrder: true,
            orderClasses: [
              "order-lg-1 order-md-0 order-sm-0 order-0",
              "order-lg-0 order-md-1 order-sm-1 order-1"
            ]
          },
          {
            changeXlTriangleOrder: true,
            orderClasses: ["order-1", "order-0"]
          }
        ];
      case "extend":
        return [
          {
            className: "card-md-order-right",
            changeXlTriangleOrder: true,
            textColor: theme.components.subWebEvents.card.text.light,
            backgroundColor:
              theme.components.subWebEvents.card.bgColor.darkGray,

            columnClasses: ["col", "col-lg-9 col"],
            orderClasses: [
              "order-lg-1 order-md-0 order-sm-0 order-0",
              "order-lg-0 order-md-1 order-sm-1 order-1"
            ]
          }
        ];
      default:
        return [
          {
            textColor: theme.components.subWebEvents.card.text.light,
            backgroundColor:
              theme.components.subWebEvents.card.bgColor.lightGray
          },
          {
            className: "card-md-order-left",
            orderClasses: [
              "order-lg-0 order-md-1 order-sm-1 order-1",
              "order-lg-1 order-md-0 order-sm-0 order-0"
            ]
          }
        ];
    }
  };

  return (
    <Fragment>
      {/*Big Image*/}
      {hasImageSection && (
        <Section paddingTop="0" paddingBottom="0">
          <div className="container">
            {isLoading ? (
              <AspectRatio ratio="sixteenByNine" />
            ) : (
              <AspectRatio ratio="sixteenByNine">
                <Picture src={imageCover} alt={alt} height="100%" />
              </AspectRatio>
            )}
          </div>
        </Section>
      )}

      {/*Cards*/}
      <Section hasHighContrastBorderTop={false}>
        <div className="container">
          <div className="row align-items-center">
            <div className="col">
              <SectionHeader
                href={href}
                marginBottom="0"
                hasLine={hasLink}
                hasLink={hasLink}
                hasContainer={false}
                textColor={
                  hasLink
                    ? theme.components.subWebEvents.linkColor
                    : theme.components.subWebEvents.color
                }
              >
                {title}
              </SectionHeader>
            </div>
            {hasButton ? (
              <div className="col-auto">
                <Link
                  color={
                    hasLink
                      ? theme.components.subWebEvents.linkColor
                      : theme.components.subWebEvents.color
                  }
                  href={buttonLink}
                  size="mediumSmall"
                  textDecoration="underline"
                  lighten
                >
                  {description}
                </Link>
              </div>
            ) : (
              <></>
            )}
          </div>

          {isLoading ? (
            <Loading />
          ) : (
            <Fragment>
              {/*Cards Container*/}
              <CardsContainer>
                {data.map((row, index) => {
                  return (
                    <div key={index} className="row no-gutters">
                      {row.cards.map((card, i) => {
                        let typeProps = getRowTypeProps(row.type);

                        return (
                          <div
                            key={i}
                            className={
                              row.type === "extend" ? "col-12" : "col-lg col-12"
                            }
                          >
                            <EventCard {...typeProps[i]} {...card} />
                          </div>
                        );
                      })}
                    </div>
                  );
                })}

                {/*
                <div className="row no-gutters">
                  <div className="col-lg col-12">
                    <EventCard
                      textColor={theme.components.subWebEvents.card.text.light}
                      backgroundColor={
                        theme.components.subWebEvents.card.bgColor.lightGray
                      }
                      image={images[0]}
                    />
                  </div>
                  <div className="col-lg col-12">
                    <EventCard
                      className="card-md-order-left"
                      orderClasses={[
                        "order-lg-0 order-md-1 order-sm-1 order-1",
                        "order-lg-1 order-md-0 order-sm-0 order-0"
                      ]}
                      image={images[1]}
                    />
                  </div>
                </div>
                <div className="row no-gutters">
                  <div className="col-lg col-12">
                    <EventCard
                      className="card-md-order-right"
                      changeXlTriangleOrder
                      orderClasses={[
                        "order-lg-1 order-md-0 order-sm-0 order-0",
                        "order-lg-0 order-md-1 order-sm-1 order-1"
                      ]}
                      image={images[2]}
                    />
                  </div>
                  <div className="col-lg col-12">
                    <EventCard
                      changeXlTriangleOrder
                      orderClasses={["order-1", "order-0"]}
                      image={images[3]}
                    />
                  </div>
                </div>
                <div className="row no-gutters">
                  <div className="col-12">
                    <EventCard
                      className="card-md-order-right"
                      changeXlTriangleOrder
                      hasEndDate={false}
                      textColor={theme.components.subWebEvents.card.text.light}
                      backgroundColor={
                        theme.components.subWebEvents.card.bgColor.darkGray
                      }
                      columnClasses={["col", "col-lg-9 col"]}
                      orderClasses={[
                        "order-lg-1 order-md-0 order-sm-0 order-0",
                        "order-lg-0 order-md-1 order-sm-1 order-1"
                      ]}
                      image={images[4]}
                    />
                  </div>
                </div>*/}
              </CardsContainer>

              {/*Load More Button If More To Load*/}
              {hasButton ? (
                <ContentContainer
                  className="d-flex justify-content-center"
                  marginTop="50px"
                >
                  <Button variant="default" as="a" href={buttonLink}>
                    {description}
                  </Button>
                </ContentContainer>
              ) : (
                <></>
              )}
            </Fragment>
          )}
        </div>
      </Section>
    </Fragment>
  );
};

