import React from "react";
import PropTypes from "prop-types";
//Styles
import styled, { withTheme } from "styled-components";
//Components
import { ScreenReaderText, Text, Section } from "../../../atoms";
import { SectionHeader, PersonCard, Error } from "../../../molecules";
import News from "../../../organisms/News";
import { withSize } from "react-sizeme";
import Loading from "./Loading";
import { compose } from "../../../../utils/utilities";
//Context
import { useConfig } from "../../../../context/ConfigContext";

const Cards = ({
  className,
  backgroundColor,
  titleProps,
  size,
  sectionTitle,
  cardBackgroundColor,
  cardTextColor,
  smPaddingTop,
  data,
  loading,
  hasError,
  theme,
  isSlider,
  ...props
}) => {
  const { formattedMessage: FormattedMessage } = useConfig();
  const newsProps = {
    backgroundColor,
    titleProps,
    textColor: theme.components.journalNews.titleColor,
    size,
    ...props
  };

  //Renders a slider only in desktop mode
  if (isSlider && size.width >= 767) {
    return (
      <News
        title={sectionTitle}
        data={data}
        loading={loading}
        hasError={hasError}
        showHeader
        hasFullHeightSlider={true}
        {...newsProps}
      />
    );
  }

  return (
    <Section
      smPaddingTop={smPaddingTop}
      className={className}
      backgroundColor={
        backgroundColor || theme.components.journalCards.backgroundColor
      }
    >
      <ScreenReaderText>
        <Text as="h2" text="Entrevistas" />
      </ScreenReaderText>
      <SectionHeader
        href={props.categoryUrl}
        textColor={theme.components.journalCards.titleColor}
        {...titleProps}
      >
        {props.resource ? (
          <FormattedMessage id={props.resource} defaultMessage={sectionTitle} />
        ) : (
          sectionTitle
        )}
      </SectionHeader>
      <div className="container">
        {loading ? (
          <Loading />
        ) : hasError ? (
          <Error />
        ) : data.listNews.length <= 0 ? (
          <div>
            <FormattedMessage
              id="app.content.empty"
              defaultMessage="No content to display"
            />
          </div>
        ) : size.width <= 767 ? (
          data.listNews.map((item, index) => {
            return (
              <div key={item.id} className="mobile-person-card">
                <PersonCard
                  imageTop={index > 0}
                  title={item.title}
                  name={item.pretitle}
                  src={item.imageCover.url}
                  alt={
                    item.imageCover.alt !== undefined ? item.imageCover.alt : ""
                  }
                  backgroundColor={
                    theme.components.journalCards.personCardBgColor ||
                    cardBackgroundColor
                  }
                  textColor={
                    theme.components.journalCards.cardTextColor || cardTextColor
                  }
                  url={item.url}
                  titleIsSerif={true}
                  size={size}
                  share={item.share}
                  history={props.history}
                />
              </div>
            );
          })
        ) : (
          <div className="row">
            {data.listNews
              .filter((items, index) =>
                size.width >= 992 ? items : index <= 1
              )
              .map(item => {
                return (
                  <div key={item.id} className="col">
                    <PersonCard
                      title={item.title}
                      name={item.pretitle}
                      src={item.imageCover.url}
                      alt={
                        item.imageCover.alt !== undefined
                          ? item.imageCover.alt
                          : ""
                      }
                      backgroundColor={
                        theme.components.journalCards.personCardBgColor ||
                        cardBackgroundColor
                      }
                      textColor={
                        theme.components.journalCards.cardTextColor ||
                        cardTextColor
                      }
                      url={item.url}
                      titleIsSerif={true}
                      size={size}
                      share={item.share}
                      history={props.history}
                    />
                  </div>
                );
              })}
          </div>
        )}
      </div>
    </Section>
  );
};

const defaults = {
  isSlider: false
};

const types = {
  sectionTitle: PropTypes.string,
  cardBackgroundColor: PropTypes.string,
  title: PropTypes.string,
  cardTextColor: PropTypes.string,
  smPaddingTop: PropTypes.string,
  className: PropTypes.string,
  backgroundColor: PropTypes.string,
  titleProps: PropTypes.object,
  size: PropTypes.object,
  data: PropTypes.oneOfType([PropTypes.object, PropTypes.array]),
