import React, { Fragment } from "react";
import PropTypes from "prop-types";
//Styles
import styled, { withTheme } from "styled-components";
//Components
import {
  Text,
  Icon,
  Picture,
  ScreenReaderText,
  HorizontalList,
  ListItem,
  Section,
  ContentContainer
} from "../../../atoms";
import { Error, MarkdownRenderer, BorderedCard } from "../../../molecules";
import JournalNews from "../../../organisms/Journal/News";
import moment from "moment";
import Loading from "./Loading";
import { withSize, SizeMe } from "react-sizeme";
import { compose } from "../../../../utils/utilities";
//Context
import { useConfig } from "../../../../context/ConfigContext";

const RelatedNewsCard = ({ data }) => {
  const BorderedCardProps = data.internalLink
    ? {
        hasHover: false,
        cardIsLink: false
      }
    : {
        href: data.url,
        target: "_blank"
      };

  return (
    <BorderedCard
      className="related-event-card"
      contentContainerPadding="20px 0"
      native={!data.internalLink}
      {...BorderedCardProps}
    >
      <div className="row no-gutters date-container">
        <div className="col-xl-12 col-auto">
          <Text
            size="large"
            fontWeight="400"
            isSerif={true}
            marginRight="15px"
            aria-label={moment(data.startDate).format("DD MMMM YYYY")}
          >
            {moment(data.startDate).format("DD MMM")}
          </Text>
        </div>

        {!moment(data.startDate).isSame(moment(data.endDate), "day") && (
          <div className="col-auto d-flex align-items-center">
            <hr />
            <Text
              size="large"
              fontWeight="400"
              isSerif={true}
              marginLeft="15px"
              aria-label={moment(data.endDate).format("DD MMMM YYYY")}
            >
              {moment(data.endDate).format("DD MMM")}
            </Text>
          </div>
        )}
      </div>

      <Text size="small">{data.title}</Text>
      <Text size="xSmall" marginTop="10px">
        {`${moment(data.startDate).format("HH")}h${moment(
          data.startDate
        ).format("mm")}`}
      </Text>
      <Text size="xSmall">{data.local}</Text>
    </BorderedCard>
  );
};

RelatedNewsCard.propTypes = {
  data: PropTypes.object.isRequired
};

const Details = ({
  className,
  data,
  loading,
  hasError,
  lang,
  size,
  theme,
  ...props
}) => {
  const { link: Link, useIntl } = useConfig();
  const intl = useIntl();

  const onAddUrl = data => {
    let items = [];
    data.listNews.map(item => {
      items.push({
        ...item,
        url: `/${lang}/${props.route}/${item.categoryID}/${item.id}`
      });
      return true;
    });

    return {
      listNews: items,
      pagination: data.pagination
    };
  };

  return (
    <Fragment>
      <Section
        className={className}
        backgroundColor={theme.components.journalDetails.backgroundColor}
        paddingTop="0"
        mdPaddingTop="70px"
        hasHighContrastBorderTop={false}
      >
        <ScreenReaderText>
          <Text as="h2" text="Detalhes" />
        </ScreenReaderText>
        <div className="container">
          {loading ? (
            <Loading />
          ) : hasError ? (
            <Error />
          ) : (
            <Fragment>
              <div className="row no-gutters">
                {/*Column*/}
                <div className="col-xl-7 offset-xl-3 col-12 offset-0">
                  {lang !== data.language && (
                    <ContentContainer marginBottom="40px">
                      <Text color="#00b2bd">
                        {`We are working in the translation of this content to ${
                          lang === "pt" ? "Portuguese" : "English"
                        }. The original version of the content is
                          presented below.`}
                        {/* <Link
                        href={`${TranslationUrl}${devMode ? demoUrl : page}`}
                        target="_blank"
                      >
                        View translated page by Google
                      </Link> */}
                      </Text>
                    </ContentContainer>
                  )}

                  <Link
                    size="small"
                    fontWeight="300"
                    lineHeight="1.5"
                    href={data.categoryUrl}
                  >
                    {data.category}
                  </Link>
                  <Text size="xLarge" fontWeight="500" isSerif={true}>
                    {data.title}
                  </Text>
                  <div className="header-social-links-container">
                    <div className="row align-items-center">
                      <div className="col">
                        <Text textTransform="lowercase">
                          {moment(data.startDate).format("DD MMMM YYYY")}
                        </Text>
                      </div>
                      <div className="col-auto" aria-hidden={true}>
                        <HorizontalList>
                          <ListItem>
                            <Link
                              display="inline-block"
                              padding="5px"
                              margin="0 0 0 5px"
                              href={data.facebook}
                              target="_blank"
                              //aria-label="Facebook"
                              tabIndex="-1"
                              title="Facebook"
                              native
                            >
                              <Icon
                                name={theme.icons.facebook}
                                size="xxSmall"
                                color={
                                  theme.components.journalDetails
                                    .socialMediaIconsColor
                                }
                              />
                            </Link>
                          </ListItem>
                          <ListItem>
                            <Link
                              display="inline-block"
                              padding="5px"
                              margin="0 0 0 5px"
                              href={data.twitter}
                              target="_blank"
                              //aria-label="Twitter"
                              tabIndex="-1"
                              title="Twitter"
                              native
                            >
                              <Icon
                                name={theme.icons.twitter}
                                size="xxSmall"
                                color={
                                  theme.components.journalDetails
                                    .socialMediaIconsColor
                                }
                              />
                            </Link>
                          </ListItem>
                          <ListItem>
                            <Link
                              display="inline-block"
                              padding="5px 0 5px 5px"
                              margin="0 0 0 5px"
                              href={data.linkedin}
                              target="_blank"
                              //aria-label="LinkedIn"
                              tabIndex="-1"
                              title="LinkedIn"
                              native
                            >
                              <Icon
                                name={theme.icons.linkedIn}
                                size="xxSmall"
                                color={
                                  theme.components.journalDetails
                                    .socialMediaIconsColor
                                }
                              />
                            </Link>
                          </ListItem>
                        </HorizontalList>
                      </div>
                    </div>
                  </div>
                </div>
              </div>

              <div className="row no-gutters">
                {size.width >= 1200 && !loading && data.event !== null && (
                  <div className="col-xl-3 col-12">
                    {/*Affiliated News Card desktop*/}
                    <RelatedNewsCard data={data.event} />
                  </div>
                )}
                <div
                  className={`col-xl-7 col-12 ${
                    !loading && data.event !== null
                      ? ""
                      : "offset-xl-3 offset-0"
                  }`}
                >
                  <Text size="mediumSmall">{data.summary}</Text>

                  <ContentContainer marginTop="40px">
                    {data.imageCover !== null && (
                      <SizeMe monitorHeight>
                        {({ size }) => {
                          return (
                            <Picture
                              src={
                                data.imageCover !== null && data.imageCover.url
                              }
                              alt={
                                data.imageCover !== null
                                  ? data.imageCover.alt
                                  : ""
                              }
                              isLocal={false}
                              hasEndpoint={false}
                              height="100%"
                              width="100%"
                              backgroundSize={
                                size.height >= 619 ? "contain" : "cover"
                              }
                              imagePosition="left"
                            />
                          );
                        }}
                      </SizeMe>
                    )}
                  </ContentContainer>

                  <ContentContainer marginTop="15px">
                    <MarkdownRenderer content={data.text} default />
                  </ContentContainer>

                  {data.files !== null && data.files.length > 0 && (
                    <div className="files-container">
                      {data.files.map((item, index) => {
                        return (
                          <Link
                            key={index}
                            display="block"
                            className="file-link"
                            padding="5px 0"
                            href={item.url}
                            target="_blank"
                            native
                          >
                            <HorizontalList display="inline-block">
                              <ListItem>
                                <Icon
                                  name={theme.icons.file}
                                  size="xSmall"
                                  color={theme.components.journalDetails.color}
                                />
                              </ListItem>
                              <ListItem>
                                <Text marginLeft="20px">{item.name}</Text>
                              </ListItem>
                            </HorizontalList>
                          </Link>
                        );
                      })}
                    </div>
                  )}

                  {/*Affiliated News Card mobile*/}
                  {size.width <= 1199 && !loading && data.event !== null && (
                    <RelatedNewsCard data={data.event} />
                  )}

                  <div className="tags-container">
                    <div className="row no-gutters">
                      <div className="col-auto order-xl-0 order-lg-0 order-1">
                        <Link
                          display="inline-block"
                          margin="0 20px 0 0"
                          padding="5px 5px 5px 0"
                          title={intl({
                            id: "app.journal.news.details.print",
                            defaultMessage: "Print"
                          })}
                          aria-label={intl({
                            id: "app.journal.news.details.print",
                            defaultMessage: "Print"
                          })}
                          onClick={e => {
                            e.preventDefault();
                            window.print();
                          }}
                        >
                          <Icon
                            name={theme.icons.fa_print}
                            size="xxSmall"
                            color={
                              theme.components.journalDetails.printIconColor
                            }
                          />
                        </Link>
                      </div>

                      <div className="col-auto order-xl-1 order-lg-1 order-2">
                        <HorizontalList display="inline-block">
                          <ListItem>
                            <Link
                              display="inline-block"
                              padding="5px"
                              margin="0 5px 0 0"
                              href={data.facebook}
                              target="_blank"
                              aria-label="Facebook"
                              title="Facebook"
                              native
                            >
                              <Icon
                                name={theme.icons.facebook}
                                size="xxSmall"
                                color={
                                  theme.components.journalDetails
                                    .socialMediaIconsColor
                                }
                              />
                            </Link>
                          </ListItem>
                          <ListItem>
                            <Link
                              display="inline-block"
                              padding="5px"
                              margin="0 5px 0 0"
                              href={data.twitter}
                              target="_blank"
                              aria-label="Twitter"
                              title="Twitter"
                              native
                            >
                              <Icon
                                name={theme.icons.twitter}
                                size="xxSmall"
                                color={
                                  theme.components.journalDetails
                                    .socialMediaIconsColor
                                }
                              />
                            </Link>
                          </ListItem>
                          <ListItem>
                            <Link
                              display="inline-block"
                              padding="5px"
                              margin="0 25px 0 0"
                              href={data.linkedin}
                              target="_blank"
                              aria-label="LinkedIn"
                              title="LinkedIn"
                              native
                            >
                              <Icon
                                name={theme.icons.linkedIn}
                                size="xxSmall"
                                color={
                                  theme.components.journalDetails
                                    .socialMediaIconsColor
                                }
                              />
                            </Link>
                          </ListItem>
                        </HorizontalList>
                      </div>

                      {data.tags !== null && data.tags.length > 0 && (
                        <div className="col-xl col-lg col-md-12 order-xl-2 order-lg-2 order-0">
                          <HorizontalList
                            display="inline-block"
                            className="tags-list"
                          >
                            <ListItem>
                              <Text
                                size="xSmall"
                                fontWeight="500"
                                marginRight="5px"
                              >
                                Tags:
                              </Text>
                            </ListItem>

                            {data.tags.map(item => {
                              return (
                                <ListItem key={item.tagId}>
                                  <Link
                                    display="inline-block"
                                    padding="5px"
                                    margin="0 5px 0 0"
                                    href={`${data.tagUrl}/${item.tagId}?tag=true?n=${item.text}`}
                                  >
                                    <Text
                                      size="xSmall"
                                      color={
                                        theme.components.journalDetails
                                          .linkColor
                                      }
                                    >
                                      {item.text}
                                    </Text>
                                  </Link>
                                </ListItem>
                              );
                            })}
                          </HorizontalList>
                        </div>
                      )}
                    </div>
                  </div>
                </div>
              </div>
            </Fragment>
          )}
        </div>
      </Section>

      {!loading && data.related !== null && data.related.length > 0 && (
        <JournalNews
          sectionTitle="Outras Notícias"
          data={onAddUrl({ listNews: data.related })}
          loading={loading}
          hasError={hasError}
          {...props}
        />
      )}
    </Fragment>
  );
};

const defaults = {
  className: "pt"
};

const types = {
  className: PropTypes.string,
  data: PropTypes.object.isRequired,
  loading: PropTypes.bool,
  hasError: PropTypes.bool,
  lang: PropTypes.string,
  route: PropTypes.string,
  hasHash: PropTypes.string,
  size: PropTypes.object,
  intl: PropTypes.object,
  theme: PropTypes.object
};

Details.propTypes = types;
Details.defaultProps = defaults;

const DetailsWithStyles = styled(Details)`
  img {
    max-height: 620px;
  }
  a {
    &:focus {
      ${props => props.theme.mixins.focus};
    }
  }
  .header-social-links-container {
    margin: 20px 0 40px 0;
  }
  .tags-container {
    margin-top: 60px;
    .files-container {
      margin-top: 20px;
    }
    .file-link {
      border-bottom: 1px solid #000;
      &:last-child {
        border-bottom: 0;
      }
    }
  }

  /*
  -- Global related event bordered card styles
  */
  .related-event-card {
    hr {
      height: 1px;
      border: none;
      width: 40px;
      background-color: ${props =>
        props.theme.components.journalDetails.borderedCardLineColor};
      transition: width 0.4s ease;
    }
    .date-container {
      margin-bottom: 30px;
    }
  }
  a.related-event-card {
    &:hover {
      hr {
        width: 60px;
      }
    }
  }

  // Large devices (desktops, more than 1200px)
  @media (min-width: 1200px) {
    /*
    -- Related event bordered card styles > 1200px
    */
    .related-event-card {
      margin-right: 55px;
      .content-container {
        padding-right: 30px;
      }
      .line {
        height: 1px;
        width: 40px;
        background-color: #000;
      }
    }
  }

  // Large devices (desktops, less than 1200px)
  @media (max-width: 1199px) {
    /*
    -- Related event bordered card styles < 1199px
    */
    .related-event-card {
      margin-top: 40px;
      .content-container {
        border: none;
        padding: 0;
      }
    }
  }

  // Medium devices (tablets, less than 992px)
  @media (max-width: 991px) {
    .tags-list {
      margin-bottom: 20px;
      a {
        padding-left: 0;
        padding-right: 0;
        margin-right: 15px;
      }
      li {
        &:first-child {
          p {
            margin-right: 10px;
          }
        }
      }
    }
  }
`;

export default compose(withTheme, withSize())(DetailsWithStyles);

//Only used for the Storybook Props Table
