        ) : data.listNews.length <= 0 ? (
          <div>
            <FormattedMessage
              id="app.content.empty"
              defaultMessage="No content to display"
            />
          </div>
        ) : (
          <Fragment>
            <div className="row container-row">
              {data.listNews
                .filter((_, i) => i <= 1)
                .map((item, index) => {
                  if (item.style === 0) {
                    return (
                      <div
                        key={item.id}
                        className="col-xl-6 col-md-6 col-12 order-md-0 order-sm-1 listing-card"
                      >
                        <FadeIn fadeIn={index > 1} height="100%">
                          <NewsCard
                            data={{
                              title: item.title,
                              url: item.url,
                              category: item.category,
                              summary: item.summary,
                              startDate: item.startDate,
                              share: { ...item.share },
                              alt:
                                item.imageCover !== null &&
                                item.imageCover.alt !== undefined
                                  ? item.imageCover.alt
                                  : "",
                              categoryUrl: item.categoryUrl,
                              imageCover:
                                item.imageCover !== null
                                  ? { ...item.imageCover }
                                  : null
                            }}
                            hasCategoryTitle={
                              Number(props.categoryId) !== item.categoryID
                            }
                            hasColAuto={false}
                            hasShareIcon={true}
                            mobileCardBgColor={
                              theme.components.journalListing
                                .mobileNewsCardBgColor
                            }
                            size={size}
                            history={props.history}
                            imageSize={272}
                            contentContainerHeight="128sx"
                            centerPlusButton={true}
                            titleIsSerif={true}
                            plusButtonSize={55}
                            plusButtonFontSize="38px"
                            linkProps={{
                              onClick: onEnter
                            }}
                          />
                        </FadeIn>
                      </div>
                    );
                  } else {
                    return (
                      <div
                        key={item.id}
                        className="col-xl-6 col-md-6 col-12 order-md-1 order-sm-0 listing-card"
                      >
                        <FadeIn fadeIn={index > 1} height="100%">
                          <PersonCard
                            title={item.title}
                            titleIsSerif={true}
                            hasSubTitle={size.width >= 992 && true}
                            subTitle={item.summary}
                            name={item.pretitle}
                            src={
                              item.imageCover !== null
                                ? item.imageCover.url
                                : null
                            }
                            alt={
                              item.imageCover !== null &&
                              item.imageCover.alt !== undefined
                                ? item.imageCover.alt
                                : ""
                            }
                            backgroundColor={
                              item.style === 2
                                ? theme.highContrast.lightBackgroundColor ||
                                  "#C7C9CB"
                                : cardBackgroundColor
                            }
                            textColor={
                              item.style === 2
                                ? theme.highContrast.text || "#000"
                                : cardTextColor
                            }
                            url={item.url}
                            size={size}
                            history={props.history}
                            linkProps={{
                              onClick: onEnter
                            }}
                          />
                        </FadeIn>
                      </div>
                    );
                  }
                })}
            </div>

            {data.listNews
              .filter((_, index) => index > 1)
              .chunk(size.width >= 992 ? 3 : 2)
              .map((res, index) => {
                return (
                  <div key={index} className="row container-row">
                    {size.width >= 767 ? (
                      <Fragment>
                        {res.map(item => {
                          if (item.style === 1 || item.style === 2) {
                            return (
                              <div
                                key={item.id}
                                className="col-xl-4 col-lg-4 col-md-6 col-12 listing-card"
                              >
                                <FadeIn fadeIn={index > 1} height="100%">
                                  <PersonCard
                                    title={item.title}
                                    subTitle={item.summary}
                                    name={item.pretitle}
                                    src={
                                      item.imageCover !== null
                                        ? item.imageCover.url
                                        : null
                                    }
                                    alt={
                                      item.imageCover !== null &&
                                      item.imageCover.alt !== undefined
                                        ? item.imageCover.alt
                                        : ""
                                    }
                                    titleIsSerif={true}
                                    backgroundColor={
                                      item.style === 2
                                        ? theme.highContrast
                                            .lightBackgroundColor || "#C7C9CB"
                                        : cardBackgroundColor
                                    }
                                    textColor={
                                      item.style === 2
                                        ? theme.highContrast.text || "#000"
                                        : cardTextColor
                                    }
                                    url={item.url}
                                    size={size}
                                    history={props.history}
                                    linkProps={{
                                      onClick: onEnter
                                    }}
                                  />
                                </FadeIn>
                              </div>
                            );
                          } else {
                            return (
                              <div
                                key={item.id}
                                className="col-xl-4 col-lg-4 col-md-6 col-12 listing-card"
                              >
                                <FadeIn fadeIn={index > 1}>
                                  <NewsCard
                                    data={{
                                      title: item.title,
                                      url: item.url,
                                      category: item.category,
                                      summary: item.summary,
                                      startDate: item.startDate,
                                      alt:
                                        item.imageCover !== null &&
                                        item.imageCover.alt !== undefined
                                          ? item.imageCover.alt
                                          : "",
                                      categoryUrl: item.categoryUrl,
                                      imageCover:
                                        item.imageCover !== null
                                          ? { ...item.imageCover }
                                          : null,
                                      share: { ...item.share }
                                    }}
                                    hasColAuto={false}
                                    hasShareIcon={true}
                                    hasCategoryTitle={
                                      Number(props.categoryId) !==
                                      item.categoryID
                                    }
                                    mobileCardBgColor={
                                      theme.components.journalListing
                                        .mobileNewsCardBgColor
                                    }
                                    size={size}
                                    history={props.history}
                                    linkProps={{
                                      onClick: onEnter
                                    }}
                                  />
                                </FadeIn>
                              </div>
                            );
                          }
                        })}
                      </Fragment>
                    ) : (
                      <Fragment>
                        <div className="col-12">
                          <div className="mini-cards">
                            {res.map(item => {
                              return (
                                <FadeIn
                                  fadeIn={index > 2}
                                  key={item.id}
                                  height="100%"
                                >
                                  <MiniHorizontalNewsCard
                                    src={
                                      item.imageCover !== null
                                        ? item.imageCover.url
                                        : null
                                    }
                                    alt={
                                      item.imageCover !== null &&
                                      item.imageCover.alt !== undefined
                                        ? item.imageCover.alt
                                        : ""
                                    }
                                    href={item.url}
                                    category={item.category}
                                    title={item.title}
                                    date={moment(item.startDate).format(
                                      "DD MMMM YYYY"
                                    )}
                                    history={props.history}
                                    linkProps={{
                                      onClick: onEnter
                                    }}
                                  />
                                </FadeIn>
                              );
                            })}
                          </div>
                        </div>
                      </Fragment>
                    )}
                  </div>
                );
              })}
          </Fragment>
        )}
      </div>
      {Object.keys(data.listNews).length < data.pagination.totalResults && (
        <div className="button-container d-flex justify-content-center">
          <Button
            variant="default"
            onClick={onLoad}
            backgroundColor={theme.components.journalListing.backgroundColor}
          >
            <FormattedMessage
              id="app.organism.button.LoadMore"
              defaultMessage="Carregar mais"
            />
          </Button>
        </div>
      )}
    </Section>
  );
};

const types = {
  className: PropTypes.string,
  backgroundColor: PropTypes.string,
  cardBackgroundColor: PropTypes.string,
  cardTextColor: PropTypes.string,
