import React, { memo, useRef, useEffect, useState } from "react";
import PropTypes from "prop-types";
//Styles
import styled, { withTheme } from "styled-components";
//Helpers
import Slider from "react-slick";
//Components
import Loading from "./Loading";
import {
  Section,
  Text,
  Button,
  Image,
  FadeIn,
  ScreenReaderText
} from "../../../atoms";
import {
  CustomArrow,
  SectionHeader,
  NewsCard,
  Error
} from "../../../molecules";
import theme from "../../../../styles/theme.semantic";
import { withSize } from "react-sizeme";
import { lighten } from "polished";
import { compose } from "../../../../utils/utilities";
//Context
import { useConfig } from "../../../../context/ConfigContext";

const JournalSlides = memo(function JournalSlides({
  className,
  showHeader,
  title,
  resource,
  textColor,
  backgroundColor,
  titleProps,
  cardBackgroundColor,
  imageBackgroundColor,
  cardTextColor,
  mobileMdTextColor,
  onAction,
  onEnter,
  data,
  loading,
  hasError,
  size,
  share,
  theme,
  ...props
}) {
  const [bgImage, setBgImage] = useState({
    imageCover: {
      url:
        "https://images.pexels.com/photos/158651/news-newsletter-newspaper-information-158651.jpeg?auto=compress&cs=tinysrgb&dpr=2&h=750&w=1260"
    }
  });
  const sliderRef = useRef(null);
  const { link: Link, formattedMessage: FormattedMessage } = useConfig();

  const onHandleTabNavigation = e => {
    if (e.key === "Tab" && e.shiftKey) {
      sliderRef.current.slickPrev();
    }

    if (e.key === "Tab") {
      sliderRef.current.slickNext();
    }
  };

  const onSetImage = (position = 0) => {
    if (data.news !== undefined) {
      const image = data.news[position];
      setBgImage(image);
    }
  };

  useEffect(() => {
    if (!loading) {
      onSetImage();
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [loading]);

  const onChangeImage = position => {
    onSetImage(position);
  };

  return (
    <Section
      className={className}
      backgroundColor={
        theme.highContrast.backgroundColor ||
        backgroundColor ||
        theme.components.journalSlides
      }
    >
      <ScreenReaderText>
        <Text as="h2" text="Notícias" />
      </ScreenReaderText>
      {showHeader && (
        <div className="container">
          <div className="row">
            <div className="col">
              <SectionHeader
                textColor={theme.highContrast.link || textColor}
                mobileMdColor={theme.highContrast.link || mobileMdTextColor}
                hasContainer={false}
                {...titleProps}
              >
                {resource ? (
                  <FormattedMessage id={resource} defaultMessage={title} />
                ) : (
                  data.dossier.title
                )}
              </SectionHeader>
            </div>
            {size.width >= 992 && (
              <div className="col-auto">
                <Link
                  color={textColor}
                  display="inline-block"
                  href={props.rightHref}
                >
                  <Text
                    as="h2"
                    size="medium"
                    fontWeight="400"
                    lineHeight="1.5"
                    color={theme.highContrast.link || textColor}
                  >
                    {props.linkResource ? (
                      <FormattedMessage
                        id={props.linkResource}
                        defaultMessage={props.linkTitle}
                      />
                    ) : (
                      props.linkTitle
                    )}
                  </Text>
                </Link>
              </div>
            )}
          </div>
        </div>
      )}
      <div className="container slides-container">
        {loading ? (
          <Loading />
        ) : hasError ? (
          <Error>
            <FormattedMessage
              id="app.content.empty"
              defaultMessage="No content to display"
            />
          </Error>
        ) : (
          <Slider
            ref={sliderRef}
            useTransform={false}
            slidesToShow={3}
            slidesToScroll={1}
            speed={500}
            infinite={false}
            centerMode={false}
            centerPadding={0}
            dots={false}
            initialSlide={0}
            customPaging={i => {
              return <button aria-label={`Notícia ${i + 1}`}>&nbsp;</button>;
            }}
            prevArrow={<CustomArrow ariaLabel="slide anterior" left darken />}
            nextArrow={<CustomArrow ariaLabel="próximo slide" darken />}
            responsive={[
              {
                breakpoint: 1199,
                settings: {
                  slidesToShow: 3
                }
              },
              {
                breakpoint: 991,
                settings: {
                  slidesToShow: 2
                }
              },
              {
                breakpoint: 767,
                settings: {
                  slidesToShow: 1
                }
              },
              {
                breakpoint: 575,
                settings: {
                  slidesToShow: 1,
                  arrows: false,
                  dots: true
                }
              }
            ]}
          >
            {data.news.map((item, index) => {
              return (
                <div key={item.id} className="journal-news-container">
                  <NewsCard
                    className={`journal-news-card ${
                      bgImage.id === item.id ? "journal-news-card-active" : ""
                    }`}
                    data={item}
                    onClick={e => onAction(e, item.url)}
                    onKeyPress={e => onEnter(e, item.url)}
                    cardBgColor={cardBackgroundColor}
                    imageBgColor={imageBackgroundColor}
                    linkColor={cardTextColor}
                    onKeyDown={onHandleTabNavigation}
                    hasShareIcon={true}
                    onMouseOver={() => onChangeImage(index)}
                    size={size}
                    share={share}
                    history={props.history}
                  />
                </div>
              );
            })}
          </Slider>
        )}

        {size.width <= 991 && (
          <div className="button-container d-flex justify-content-center">
            <Button
              variant="default"
              onClick={() => window.location.replace(props.rightHref)}
            >
              {props.linkResource ? (
                <FormattedMessage
                  id={props.linkResource}
                  defaultMessage={props.linkTitle}
                />
              ) : (
                props.linkTitle
              )}
            </Button>
          </div>
        )}
      </div>

      {size.width >= 992 && (
        <FadeIn key={bgImage.imageCover.url}>
          <Image
            src={bgImage.imageCover.url}
            height="100%"
            width="100%"
            alt={
              bgImage.imageCover.alt !== undefined ? bgImage.imageCover.alt : ""
            }
          />
        </FadeIn>
      )}
    </Section>
  );
});

