    height: 398px;
    margin-top: 2px;
    margin-bottom: 2px;
  }

  .slick-prev {
    left: -25px;
  }
  .slick-next {
    right: -25px;
  }
  .slick-next,
  .slick-prev {
    width: auto;
    height: 36px;
    top: 100px;
  }

  .slick-next:before,
  .slick-prev:before {
    content: none;
  }

  .slick-dots {
    width: 100%;
    bottom: 0;
    position: relative;
    margin-top: 10px;
    text-align: right;
    padding: 0 15px;
  }

  .slick-dots li {
    margin: 0 5px;
    :last-child {
      margin-right: 0;
    }
  }

  .slick-dots li,
  .slick-dots li button {
    width: 8px;
    height: 8px;
  }

  .slick-dots li button:before {
    content: "";
    background-color: ${props =>
      props.theme.components.reactSlickSlider.buttonBgColor};
    width: 8px;
    height: 8px;
  }

  .button-container {
    margin-top: 70px;
  }

  // Large devices (desktops, 992px and up to 1199)
  @media (min-width: 992px) and (max-width: 1199px) {
    .slick-prev {
      left: -15px;
    }
    .slick-next {
      right: -15px;
    }
  }
`;

